import React, { useState } from 'react';
import Razorpay from 'react-razorpay'; // Make sure you have the correct package installed

const PaymentPage = () => {
  const [selectedOption, setSelectedOption] = useState(null);

  const handlePayment = () => {
    // Handle payment based on the selected option
    if (selectedOption === 'razorpay') {
      // Perform Razorpay payment integration
      // You should replace the following with actual Razorpay SDK integration
      alert('Processing Razorpay payment...');
    } else if (selectedOption === 'phonepe') {
      // Perform PhonePe payment integration
      // You should replace the following with actual PhonePe SDK integration
      alert('Processing PhonePe payment...');
    } else {
      alert('Please select a payment option');
    }
  };

  const options = {
    key: 'YOUR_RAZORPAY_KEY',
    amount: 1000,
    name: 'Your Company Name',
    description: 'Product/Service Description',
    image: 'your_logo_url',
    handler: function (response) {
      alert(response.razorpay_payment_id);
    },
    prefill: {
      name: 'Your Name',
      email: 'your_email@example.com',
      contact: 'Your Contact Number',
    },
    notes: {
      address: 'Your Address',
    },
    theme: {
      color: '#F37254',
    },
  };

  return (
    <div>
      <h1>Select Payment Option:</h1>
      <div>
        <label>
          <input
            type="radio"
            name="paymentOption"
            value="razorpay"
            onChange={() => setSelectedOption('razorpay')}
          />
          Razorpay
        </label>
      </div>
      <div>
        <label>
          <input
            type="radio"
            name="paymentOption"
            value="phonepe"
            onChange={() => setSelectedOption('phonepe')}
          />
          PhonePe
        </label>
      </div>

      <button onClick={handlePayment}>Proceed to Pay</button>

      {selectedOption === 'razorpay' && <Razorpay options={options} />}
    </div>
  );
};

export default PaymentPage;
