// Import necessary libraries and components
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useUserContext } from './UserContext';
import { usePropertyContext } from '../InsuranceForms/PropertyContext';
import './TotalInfo.css';

function TotalInfo() {
  // State variables for form input fields
  const [FullNamePrefix, setFullNamePrefix] = useState('');
  const [FullName, setFullName] = useState('');
  const [PanCard, setPanCard] = useState('');
  const [DateofBirth, setDateofBirth] = useState('');
  const [FlatNo, setFlatNo] = useState('');
  const [AreaStreet, setAreaStreet] = useState('');
  const [FlatNo1, setFlatNo1] = useState('');
  const [AreaStreet1, setAreaStreet1] = useState('');
  const [pincode, setPincode] = useState('');
  const [pincodee, setPincodee] = useState('');
  const [IsCurrentAddress, setIsCurrentAddress] = useState('');
  

  // State variable for displaying a message
  const [message, setMessage] = useState('');

  // State variable for error messages
  const [errorMessages, setErrorMessages] = useState({
    FullName: '',
    PanCard: '',
    DateofBirth: '',
    FlatNo: '',
    AreaStreet: '',
    CurrentAddress: '',
    pincode: '',
    pincodee: '',
    AreaStreet1: '', // Added for the new AreaStreet field
  });

  // Function to validate individual form fields
  const validateField = (fieldName, value, regex, errorMessage) => {
    if (!value || (regex && !regex.test(value))) {
      setErrorMessages((prevErrors) => ({
        ...prevErrors,
        [fieldName]: errorMessage,
      }));
      return false;
    } else if (fieldName === 'DateofBirth') {
      const age = calculateAge(value);
      if (age < 21 || age >= 100) {
        setErrorMessages((prevErrors) => ({
          ...prevErrors,
          [fieldName]: 'Age must be between 21 and 100 years.',
        }));
        return false;
      }
    } else {
      setErrorMessages((prevErrors) => ({ ...prevErrors, [fieldName]: '' }));
      return true;
    }
  };

  // Function to validate all form inputs
  const validateInputs = () => {
    const panCardRegex = /^[A-Z]{5}[0-9]{4}[A-Z]$/;
    const dateOfBirthRegex = /^\d{4}-\d{2}-\d{2}$/; // Modified regex for date format

    let isValid = true;

    isValid = isValid && validateField('FullName', FullName, /.+/, 'Please enter your full name.');
    isValid = isValid && validateField('PanCard', PanCard, panCardRegex, 'Please enter a valid PanCard.');
    isValid = isValid && validateField('DateofBirth', DateofBirth, dateOfBirthRegex, 'Please enter a valid date of birth.');
    isValid = isValid && validateField('pincode', pincode, /^\d{6}$/, 'Please enter a valid pincode.');
    isValid = isValid && validateField('FlatNo', FlatNo, /^[A-Za-z0-9\s]+$/, 'Please enter a valid flat number.');
    isValid = isValid && validateField('AreaStreet', AreaStreet, /^[A-Za-z0-9\s]+$/, 'Please enter a valid area or street.');

    if (IsCurrentAddress === 'no') {
      isValid = isValid && validateField('CurrentAddress', AreaStreet1, /^[A-Za-z0-9\s]+$/, 'Please enter a valid current address.');
      isValid = isValid && validateField('pincodee', pincodee, /^\d{6}$/, 'Please enter a valid pincode.');
      isValid = isValid && validateField('AreaStreet1', AreaStreet1, /^[A-Za-z0-9\s]+$/, 'Please enter a valid area or street.'); // Added for the new AreaStreet field
    }

    return isValid;
  };

  // Function to handle input changes
  const handleInputChange = (fieldName, value, regex, errorMessage) => {
    switch (fieldName) {
      case 'FullNamePrefix':
        setFullNamePrefix(value);
        break;
      case 'FullName':
        setFullName(value);
        break;
      case 'PanCard':
        setPanCard(value);
        break;
      case 'DateofBirth':
        setDateofBirth(value);
        validateField('DateofBirth', value, /^\d{4}-\d{2}-\d{2}$/, 'Please enter a valid date of birth.');
        break;
      case 'FlatNo':
        setFlatNo(value);
        break;
      case 'AreaStreet':
        setAreaStreet(value);
        break;
      case 'FlatNo1':
        setFlatNo1(value);
        break;
      case 'AreaStreet1':
        setAreaStreet1(value);
        break;
      case 'CurrentAddress':
        setIsCurrentAddress(value);
        break;
      case 'pincodee':
        setPincodee(value);
        break;
      case 'pincode':
        setPincode(value);
        break;
      default:
        break;
    }

    validateField(fieldName, value, regex, errorMessage);
  };

  // Function to handle form submission
  const handleProceed = () => {
    const isValid = validateInputs();

    if (isValid) {
      console.log('Proceeding to the next step...');
      setMessage('Your custom message here.');

      // Redirect to the next page (you can replace it with your navigation logic)
      // Example: navigate('/payment');
    } else {
      console.log('Form submission failed. Please check the fields.');
    }
  };

  // Function to calculate age from the given date of birth
  const calculateAge = (dob) => {
    const currentDate = new Date();
    const birthDate = new Date(dob);
    let age = currentDate.getFullYear() - birthDate.getFullYear();
    const monthDiff = currentDate.getMonth() - birthDate.getMonth();

    if (monthDiff < 0 || (monthDiff === 0 && currentDate.getDate() < birthDate.getDate())) {
      age--;
    }

    return age;
  };
  return (
    <div className='org12'>
      <h2>Fill the details</h2>
      <div className='forDispFlex'>
        <div className='fortablecls'>
          <h3>user Details</h3>
          <table className='fortable container '>
            <tr>
              <td className='tbfont'>FullName:</td>
              <td>
                <select
                  value={FullNamePrefix}
                  onChange={(e) => setFullNamePrefix(e.target.value)}
                >
                  <option>Mr.</option>
                  <option>Mrs.</option>
                  <option>Ms.</option>
                </select>
              </td>
              <td className='tbfont1'>
                {FullNamePrefix} {userData.FullName}
              </td>
            </tr>
            <tr>
              <td className='tbfont'>MobileNo</td>
              <td>:</td>
              <td className='tbfont1'>{userData.Mobile}</td>
            </tr>
            <tr>
              <td className='tbfont'>Email.Id</td>
              <td>:</td>
              <td className='tbfont1'>{userData.Email}</td>
            </tr>
          </table>
          <div>
            <h3>Premium Data</h3>
            <table className='fortable container '>
              <tr>
                <th></th>
                <th></th>
                <th></th>
              </tr>
              <tr>
                <th className='tbfont'>PropertyValue</th>
                <th>: </th>
                <th className='tbfont1'>{propertyValue}</th>
              </tr>
              <tr>
                <th className='tbfont'>CarpetValue</th>
                <th>: </th>
                <th className='tbfont1'>{carpetArea}</th>
              </tr>
              <tr>
                <th className='tbfont'>BuildingAge</th>
                <th>:</th>
                <th className='tbfont1'>{buildingAge} years</th>
              </tr>
              <tr>
                <th className='tbfont'>Total</th>
                <th>:</th>
                <th className='tbfont1'>
                  <h6>{popupMessage}</h6>
                </th>
              </tr>
            </table>
          </div>
        </div>

        <div className='forinner11d'>
          <form className='forinner11 container'>
            <h3>Enter PanCard details</h3>

            <div className='form-group11 container'>
              <div>FullName:</div>
              <div className='form-group11Input container'>
                <input
                  type='text'
                  className=''
                  placeholder='Enter your full name'
                  value={FullName}
                  onChange={(e) => handleInputChange('FullName', e.target.value, /.+/, 'Please enter your full name.')}
                />
                <p className='error-message11' style={{ color: 'red', textAlign: 'center' }}>
                  {errorMessages.FullName}
                </p>
              </div>
            </div>

            <div className='form-group11 container'>
              <div>PanCard:</div>
              <div className='form-group11Input container'>
                <input
                  type='text'
                  className=''
                  placeholder='ex: ABCDE1234F'
                  value={PanCard}
                  onChange={(e) =>
                    handleInputChange('PanCard', e.target.value, /^[A-Z]{5}[0-9]{4}[A-Z]$/, 'Please enter a valid PanCard.')
                  }
                />
                <p className='error-message11' style={{ color: 'red', textAlign: 'center' }}>{errorMessages.PanCard}</p>
              </div>
            </div>

            <div className='form-group11 container'>
      <div>
       DateofBirth:
      </div>
      <div className='form-group11Input container'>
      <input
          type='date'
          className=''
          placeholder='ex: dd/mm/yyyy'
          value={DateofBirth}
          onChange={(e) =>
            handleInputChange('dateOfBirth', e.target.value, /^\d{1,2}\/\d{1,2}\/\d{4}$/, 'Please enter a valid date of birth.')
          }
        />
        
        <p className='error-message11' style={{ color: 'red', textAlign: 'center' }}>
          {errorMessages.DateofBirth}
        </p>
      </div>
    </div>

            <h3>property details</h3>
            
            <div className='form-group11 container'>
                    <div className='form-group'>
                      <label>
                        Enter Pincode <span style={{ color: 'red' }}>*</span> :
                      </label>
                      <input
                        type='text'
                        className='form-control'
                        placeholder='ex : 515001'
                        value={pincode}
                        onChange={(e) => setPincode(e.target.value)}
                      />
                      <p className='error-message'>{errorMessages.pincode}</p>
                    </div>
                  </div>

            <div className='form-group11 container'>
              <div>FlatNo/House No:</div>
              <div className='form-group11Input container'>
                <input
                  type='text'
                  className=''
                  placeholder='ex: 123'
                  value={FlatNo}
                  onChange={(e) =>
                    handleInputChange('FlatNo', e.target.value, /^[A-Za-z0-9\s]+$/, 'Please enter a valid flat number.')
                  }
                />
                <p className='error-message11' style={{ color: 'red', textAlign: 'center' }}>
                  {errorMessages.FlatNo}
                </p>
              </div>
            </div>

            <div className='form-group11 container'>
              <div>Area/Street:</div>
              <div className='form-group11Input container'>
                <input
                  type='text'
                  className=''
                  placeholder='ex: Main Street'
                  value={AreaStreet}
                  onChange={(e) =>
                    handleInputChange('AreaStreet', e.target.value, /^[A-Za-z0-9\s]+$/, 'Please enter a valid area or street.')
                  }
                />
                <p className='error-message11' style={{ color: 'red', textAlign: 'center' }}>
                  {errorMessages.AreaStreet}
                </p>
              </div>
            </div>

            <div className='form-group11 container'>
              <div>Is the address mentioned above your current address:</div>
              <div className='form-group11Input container'>
                <select onChange={(e) => setIsCurrentAddress(e.target.value)}>
                  <option value=''>Select</option>
                  <option value='yes'>Yes</option>
                  <option value='no'>No</option>
                </select>
              </div>
            </div>

            {IsCurrentAddress === 'no' && (
              <div>
                <div className='form-group11 container'>
                  <hr />
                  <h3>Current Address:</h3>
                  <br />
                  <div className='form-group11 container'>
                    <div className='form-group'>
                      <label>
                        Enter Pincode <span style={{ color: 'red' }}>*</span> :
                      </label>
                      <input
                        type='text'
                        className='form-control'
                        placeholder='ex : 515001'
                        value={pincodee}
                        onChange={(e) => setPincodee(e.target.value)}
                      />
                      <p className='error-message'>{errorMessages.pincode}</p>
                    </div>
                  </div>
                  <div>FlatNo/House No:</div>
                  <div className='form-group11Input container'>
                    <input
                      type='text'
                      className=''
                      placeholder='ex: 123'
                      value={FlatNo1}
                      onChange={(e) =>
                        handleInputChange('FlatNo1', e.target.value, /^[A-Za-z0-9\s]+$/, 'Please enter a valid flat number.')
                      }
                    />
                    <p className='error-message11' style={{ color: 'red', textAlign: 'center' }}>
                      {errorMessages.FlatNo1}
                    </p>
                  </div>

                  <div className='form-group11 container'>
                    <div>Area/Street:</div>
                    <div className='form-group11Input container'>
                      <input
                        type='text'
                        className=''
                        placeholder='ex: Main Street'
                        value={AreaStreet1}
                        onChange={(e) =>
                          handleInputChange('AreaStreet1', e.target.value, /^[A-Za-z0-9\s]+$/, 'Please enter a valid area or street.')
                        }
                      />
                      <p className='error-message11' style={{ color: 'red', textAlign: 'center' }}>
                        {errorMessages.AreaStreet1}
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            )}
            <button className='btn btn-success my-2 ' type='button' onClick={handleProceed}>
              Proceed
            </button>
          </form>
        </div>
        <div></div>
      </div>
    </div>
  );
}

export default TotalInfo;
