import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './TotalInfo.css';
import { useNavigate, useLocation } from 'react-router-dom';

function TotalInfo() {
  const { state } = useLocation();
  const mobile = state.Mobile;
  
  const [propertyDetails, setPropertyDetails] = useState([]);
  const [userDetails, setUserDetails] = useState([]);
  const [navigateTo, setNavigateTo] = useState('');
  
  // Form inputs and error messages
  const [inputs, setInputs] = useState({
    fullName: '',
    panCard: '',
    dateOfBirth: '',
    flatNo: '',
    areaStreet: '',
    pincode: '',
    isCurrentAddress: '',
    currentAddress: {
      pincode: '',
      flatNo: '',
      areaStreet: '',
    }
  });
  
  const [errors, setErrors] = useState({});
  
  const navigate = useNavigate();
  
  // Fetch property details and user details
  useEffect(() => {
    if (mobile) {
      fetchPropertyDetails(mobile);
      fetchUserDetails(mobile);
    }
  }, [mobile]);
  
  const fetchPropertyDetails = async (mobile) => {
    try {
      const response = await axios.get(`http://localhost:9855/api/property/${mobile}`);
      setPropertyDetails(response.data);
      setInputs((prev) => ({ ...prev, pincode: response.data.pincode }));
    } catch (error) {
      console.error('Error fetching property details:', error);
    }
  };
  
  const fetchUserDetails = async (mobile) => {
    try {
      const response = await axios.get(`http://localhost:9855/api/signin/${mobile}`);
      setUserDetails(response.data);
    } catch (error) {
      console.error('Error fetching user details:', error);
    }
  };
  
  // Universal validation function
  const universalValidation = (value, type) => {
    let regex;
    switch (type) {
      case 'alphanumeric':
        regex = /^[A-Za-z0-9\s]+$/;
        break;
      case 'panCard':
        regex = /^[A-Z]{5}[0-9]{4}[A-Z]$/;
        break;
      case 'date':
        regex = /^\d{1,2}\/\d{1,2}\/\d{4}$/;
        break;
      case 'pincode':
        regex = /^\d{6}$/;
        break;
      case 'mobileNumber':
        regex = /^(\+91-?)?[6-9]\d{9}$/;
        break;
      default:
        return false;
    }
    return regex.test(value);
  };
  
  // Validate input fields
  const validateInputs = () => {
    const newErrors = {};
    let isValid = true;
    
    // Validate Full Name
    if (!universalValidation(inputs.fullName, 'alphanumeric')) {
      newErrors.fullName = 'Please enter a valid full name (letters and spaces only).';
      isValid = false;
    }
    
    // Validate PAN Card
    if (!universalValidation(inputs.panCard, 'panCard')) {
      newErrors.panCard = 'Please enter a valid PAN Card number.';
      isValid = false;
    }
    
    // Validate Date of Birth
    if (!universalValidation(inputs.dateOfBirth, 'date')) {
      newErrors.dateOfBirth = 'Please enter a valid date of birth (dd/mm/yyyy).';
      isValid = false;
    } else {
      const birthDate = new Date(inputs.dateOfBirth.split('/').reverse().join('/'));
      const today = new Date();
      const age = today.getFullYear() - birthDate.getFullYear();
      if (age < 21 || age > 100) {
        newErrors.dateOfBirth = 'Age must be between 21 and 100 years.';
        isValid = false;
      }
    }
    
    // Validate Pincode
    if (!universalValidation(inputs.pincode, 'pincode')) {
      newErrors.pincode = 'Please enter a valid 6-digit Pincode.';
      isValid = false;
    }
    
    // Validate Flat No and Area Street
    if (!universalValidation(inputs.flatNo, 'alphanumeric')) {
      newErrors.flatNo = 'Please enter a valid flat number.';
      isValid = false;
    }
    if (!universalValidation(inputs.areaStreet, 'alphanumeric')) {
      newErrors.areaStreet = 'Please enter a valid area or street.';
      isValid = false;
    }
    
    // Validate Current Address if different from permanent address
    if (inputs.isCurrentAddress === 'no') {
      const currentAddress = inputs.currentAddress;
      if (!universalValidation(currentAddress.pincode, 'pincode')) {
        newErrors.currentAddressPincode = 'Please enter a valid 6-digit Pincode.';
        isValid = false;
      }
      if (!universalValidation(currentAddress.flatNo, 'alphanumeric')) {
        newErrors.currentAddressFlatNo = 'Please enter a valid flat number.';
        isValid = false;
      }
      if (!universalValidation(currentAddress.areaStreet, 'alphanumeric')) {
        newErrors.currentAddressAreaStreet = 'Please enter a valid area or street.';
        isValid = false;
      }
    }
    
    setErrors(newErrors);
    return isValid;
  };
  
  // Handle input changes
  const handleInputChange = (fieldName, value) => {
    setInputs((prev) => {
      if (fieldName.includes('currentAddress')) {
        return {
          ...prev,
          currentAddress: {
            ...prev.currentAddress,
            [fieldName.replace('currentAddress.', '')]: value,
          },
        };
      } else {
        return {
          ...prev,
          [fieldName]: value,
        };
      }
    });
  };
  
  // Handle form submission
  const handleProceed = async () => {
    if (validateInputs()) {
      const data = {
        ...inputs,
        currentAddress: inputs.isCurrentAddress === 'no' ? inputs.currentAddress : null,
      };
      try {
        const response = await axios.post('http://localhost:9855/api/user-address/add', data);
        console.log('Data sent successfully:', response.data);
        navigate('/payment');
      } catch (error) {
        console.error('Error while sending data:', error);
      }
    } else {
      console.log('Form submission failed. Please check the fields.');
    }
  };
  
  return (
    <div className="org12">
      <div className="h2">
        {userDetails ? (
          <h4>Welcome {userDetails.fullName}</h4>
        ) : (
          <p>Loading...</p>
        )}
        <h2>Fill the details</h2>
      </div>

      <div className="row">
        {/* User Details & Premium Data */}
        <div className="col-md-2">
          <h3>User Details & Premium Data</h3>
          <div className="container">
            {propertyDetails || userDetails ? (
              <table>
                <tbody>
                  <tr>
                    <td><strong>Mobile:</strong></td>
                    <td>{propertyDetails.mobile}</td>
                  </tr>
                  <tr>
                    <td><strong>Email:</strong></td>
                    <td>{userDetails.email}</td>
                  </tr>
                  <tr>
                    <td><strong>MarketValue:</strong></td>
                    <td>{propertyDetails.currentMarketValue}</td>
                  </tr>
                  <tr>
                    <td><strong>Carpet Area:</strong></td>
                    <td>{propertyDetails.carpetArea}</td>
                  </tr>
                  <tr>
                    <td><strong>Pincode:</strong></td>
                    <td>{propertyDetails.pincode}</td>
                  </tr>
                  <tr>
                    <td><strong>Building Age:</strong></td>
                    <td>{propertyDetails.buildingAge}</td>
                  </tr>
                  <tr>
                    <td><strong>Property Effect:</strong></td>
                    <td>{propertyDetails.propertyEffect}</td>
                  </tr>
                  <tr>
                    <td><strong>Security 24x7:</strong></td>
                    <td>{propertyDetails.security24x7}</td>
                  </tr>
                  <tr>
                    <td><strong>Is Salaried:</strong></td>
                    <td>{propertyDetails.isSalaried}</td>
                  </tr>
                  <tr>
                    <td><strong>Premium:</strong></td>
                    <td>{propertyDetails.premium}</td>
                  </tr>
                </tbody>
              </table>
            ) : (
              <p>Loading...</p>
            )}
          </div>
        </div>

        {/* Form */}
        <div className="col-md-10">
          <div className="form-container">
            <form className="form container">
              <h3>Enter PAN Card Details</h3>

              {/* Full Name Input */}
              <div className="form-group">
                <label>Full Name:</label>
                <input
                  type="text"
                  placeholder="Enter your full name"
                  value={inputs.fullName.toUpperCase()}
                  onChange={(e) => handleInputChange('fullName', e.target.value)}
                />
                <p className="error-message" style={{ color: 'red', textAlign: 'center' }}>
                  {errors.fullName}
                </p>
              </div>

              {/* PAN Card Input */}
              <div className="form-group">
                <label>PAN Card:</label>
                <input
                  type="text"
                  placeholder="ex: ABCDE1234F"
                  value={inputs.panCard.toUpperCase()}
                  onChange={(e) =>
                    handleInputChange('panCard', e.target.value)
                  }
                />
                <p className="error-message" style={{ color: 'red', textAlign: 'center' }}>
                  {errors.panCard}
                </p>
              </div>

              {/* Date of Birth Input */}
              <div className="form-group">
                <label>Date of Birth:</label>
                <input
                  type="text"
                  placeholder="ex: dd/mm/yyyy"
                  value={inputs.dateOfBirth}
                  onChange={(e) => handleInputChange('dateOfBirth', e.target.value)}
                />
                <p className="error-message" style={{ color: 'red', textAlign: 'center' }}>
                  {errors.dateOfBirth}
                </p>
              </div>

              {/* Pincode Input */}
              <div className="form-group">
                <label>Pincode:</label>
                <input
                  type="text"
                  placeholder="ex: 515001"
                  value={inputs.pincode}
                  onChange={(e) =>
                    handleInputChange('pincode', e.target.value)
                  }
                  maxLength={6}
                />
                <p className="error-message" style={{ color: 'red', textAlign: 'center' }}>
                  {errors.pincode}
                </p>
              </div>

              {/* Flat No Input */}
              <div className="form-group">
                <label>Flat No/House No:</label>
                <input
                  type="text"
                  placeholder="ex: 123"
                  value={inputs.flatNo}
                  onChange={(e) =>
                    handleInputChange('flatNo', e.target.value)
                  }
                />
                <p className="error-message" style={{ color: 'red', textAlign: 'center' }}>
                  {errors.flatNo}
                </p>
              </div>

              {/* Area/Street Input */}
              <div className="form-group">
                <label>Area/Street:</label>
                <input
                  type="text"
                  placeholder="ex: Main Street"
                  value={inputs.areaStreet}
                  onChange={(e) =>
                    handleInputChange('areaStreet', e.target.value)
                  }
                />
                <p className="error-message" style={{ color: 'red', textAlign: 'center' }}>
                  {errors.areaStreet}
                </p>
              </div>

              {/* Current Address */}
              <div className="form-group">
                <label>Is the address mentioned above your current address?</label>
                <select value={inputs.isCurrentAddress} onChange={(e) => handleInputChange('isCurrentAddress', e.target.value)}>
                  <option value="">Select</option>
                  <option value="yes">Yes</option>
                  <option value="no">No</option>
                </select>
              </div>

              {/* Conditional Rendering for Current Address */}
              {inputs.isCurrentAddress === 'no' && (
                <div className="current-address">
                  <h4>Current Address:</h4>

                  {/* Pincode Input */}
                  <div className="form-group">
                    <label>Current Pincode:</label>
                    <input
                      type="text"
                      placeholder="ex: 515001"
                      value={inputs.currentAddress.pincode}
                      onChange={(e) =>
                        handleInputChange('currentAddress.pincode', e.target.value)
                      }
                      maxLength={6}
                    />
                    <p className="error-message" style={{ color: 'red', textAlign: 'center' }}>
                      {errors.currentAddressPincode}
                    </p>
                  </div>

                  {/* Flat No Input */}
                  <div className="form-group">
                    <label>Flat No/House No:</label>
                    <input
                      type="text"
                      placeholder="ex: 123"
                      value={inputs.currentAddress.flatNo}
                      onChange={(e) =>
                        handleInputChange('currentAddress.flatNo', e.target.value)
                      }
                    />
                    <p className="error-message" style={{ color: 'red', textAlign: 'center' }}>
                      {errors.currentAddressFlatNo}
                    </p>
                  </div>

                  {/* Area/Street Input */}
                  <div className="form-group">
                    <label>Area/Street:</label>
                    <input
                      type="text"
                      placeholder="ex: Main Street"
                      value={inputs.currentAddress.areaStreet}
                      onChange={(e) =>
                        handleInputChange('currentAddress.areaStreet', e.target.value)
                      }
                    />
                    <p className="error-message" style={{ color: 'red', textAlign: 'center' }}>
                      {errors.currentAddressAreaStreet}
                    </p>
                  </div>
                </div>
              )}

              {/* Proceed Button */}
              <div className="text-center">
                <button className="btn btn-success" type="button" onClick={handleProceed}>
                  Proceed
                </button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}

export default TotalInfo;
