import React, { useState, useEffect } from 'react';
import './PopupModal.css';
import LoginPage from '../HomePage/LoginPage';
import SignInPage from './SignInPage';
import { usePropertyContext } from '../InsuranceForms/PropertyContext';
import axios from 'axios'; // Import axios for making HTTP requests

const PopupModal = ({ message, onClose }) => {
  const { popupMessage, updatePopupMessage } = usePropertyContext();
  const [isSignIn, setIsSignIn] = useState(true);

  const toggleView = () => {
    setIsSignIn((prev) => !prev);
  };

  useEffect(() => {
    // Save the popupMessage to the database when the modal is opened or when the message prop changes
    savePopupMessage(message);
    // Update the context with the popupMessage
    updatePopupMessage(message);
  }, [message, updatePopupMessage]);

  // Function to save the popupMessage to the database
  const savePopupMessage = async (message) => {
    try {
      // Make a POST request to your backend API endpoint to save the popupMessage
      await axios.post('http://localhost:9856/api/popupmessages', { message });
    } catch (error) {
      console.error('Error saving popup message:', error);
    }
  };

  return (
    <div className="popup-modal">
      <div className="popup-content">
        <h3 className='forMsg'>{popupMessage}</h3>
        <div className="container">
          <div className="box">
            <h2>{isSignIn ? 'Sign In' : 'Login'}</h2>
            {isSignIn ? <SignInPage /> : <LoginPage />}
            <p onClick={toggleView} className="toggle-link text-primary font-weight-bold text-center" style={{ cursor: 'pointer' }}>
              {isSignIn ? 'Switch to Login' : 'Switch to Sign In'}
            </p>
          </div>
        </div>
        <button className='btn btn-danger' onClick={onClose}> Close</button>
      </div>
    </div>
  );
};

export default PopupModal;
