import React, { useState } from 'react';
import { Link } from 'react-router-dom';

// Move validateField outside of validateInputs
const validateField = (fieldName, value, regex, errorMessage, setErrorMessages) => {
  if (!value || (regex && !regex.test(value))) {
    setErrorMessages((prevErrors) => ({
      ...prevErrors,
      [fieldName]: errorMessage,
    }));
    return false;
  } else {
    setErrorMessages((prevErrors) => ({ ...prevErrors, [fieldName]: '' }));
    return true;
  }
};

function SignInPage() {
  const [FullName, setFullName] = useState('');
  const [Mobile, setMobile] = useState('');
  const [Email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessages, setErrorMessages] = useState({
    FullName: '',
    Mobile: '',
    Email: '',
    password: '',
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    const isValid = validateInputs();

    if (isValid) {
      console.log('Form submitted:', {
        FullName,
        Mobile,
        Email,
        password,
      });
      // No need to programmatically navigate, Link will handle it
    } else {
      console.log('Form submission failed. Please check the fields.');
    }
  };

  const validateInputs = () => {
    const nameRegex = /^[A-Za-z. ]{3,}$/;
    const mobileRegex = /^[9876][0-9]{0,9}$/;
    const emailRegex = /^[^\s@]+@[^\s@]+\.(com|in)$/;
    const passwordRegex = /^[A-Za-z.0-9!@#$%^&*]{0,}$/;

    let isValid = true;

    // Use validateField here
    isValid = isValid && validateField('FullName', FullName, nameRegex, 'Please enter a valid full name.', setErrorMessages);
    isValid = isValid && validateField('Mobile', Mobile, mobileRegex, 'Please enter a valid 10-digit mobile number.', setErrorMessages);
    isValid = isValid && validateField('Email', Email, emailRegex, 'Please enter a valid email address.', setErrorMessages);
    isValid = isValid && validateField(
      'password',
      password,
      passwordRegex,
      'Password must be at least 8 characters, contain at least one letter, one number, and one special character.',
      setErrorMessages
    );

    return isValid;
  };

  return (
    <div className='org'>
      <h2>SignIn forher</h2>
      <form className='forinner container' onSubmit={handleSubmit}>
        {/* ... (unchanged part of the component) */}
      </form>
    </div>
  );
}

export default SignInPage;
