import React, { useState } from 'react';
import './PropertyForm.css'; // Your custom CSS file
import 'bootstrap/dist/css/bootstrap.min.css'; // Bootstrap CSS
import { Link } from 'react-router-dom';

function PropertyForm() {
  const [currentMarketValue, setCurrentMarketValue] = useState('');
  const [carpetArea, setCarpetArea] = useState('');
  const [pincode, setPincode] = useState('');
  const [buildingAge, setBuildingAge] = useState('0');
  const [propertyEffect, setPropertyEffect] = useState('No');
  const [security24x7, setSecurity24x7] = useState('No');
  const [isSalaried, setIsSalaried] = useState('No');
  const [errorMessages, setErrorMessages] = useState({
    currentMarketValue: '',
    carpetArea: '',
    pincode: '',
    buildingAge: '',
  });
  const [totalSum, setTotalSum] = useState(0);

  const handleProceed = () => {
    const isValid = validateInputs();

    if (isValid) {
      const propertyValue = parseInt(currentMarketValue, 10);
      const areaValue = parseInt(carpetArea, 10);
      const pincodeValue = parseInt(pincode, 10);
      const buildingAgeValue = parseInt(buildingAge, 10);
      const additionalCost = calculateAdditionalCost(buildingAgeValue);

      const formData = {
        currentMarketValue: propertyValue,
        carpetArea: areaValue,
        pincode: pincodeValue,
        buildingAge: buildingAgeValue,
        propertyEffect,
        security24x7,
        isSalaried,
        additionalCost,
      };

      setTotalSum((prevSum) => prevSum + calculateTotal(formData));

      clearFormInputs();

      console.log('Form submitted:', formData);
    } else {
      console.log('Form submission failed. Please enter valid numbers.');
    }
  };

  const handleCalculateSum = () => {
    alert(`Total Sum: ${totalSum}`);
  };

  const calculateTotal = (formData) => {
    return (
      formData.currentMarketValue +
      formData.carpetArea +
      formData.pincode +
      formData.buildingAge +
      (formData.propertyEffect === 'Yes' ? 200 : 300) +
      (buildingAge >= 0 && buildingAge <= 5 ? 100 : 0) +
      (buildingAge > 5 && buildingAge <= 10 ? 200 : 0) +
      (buildingAge > 10 && buildingAge <= 15 ? 300 : 0) +
      (buildingAge > 15 && buildingAge <= 20 ? 400 : 0) +
      (buildingAge > 20 && buildingAge <= 25 ? 500 : 0) +
      (buildingAge > 25 && buildingAge <= 30 ? 600 : 0)
    );
  };

  const validateInputs = () => {
    const numberRegex = /^\d+$/;
    let isValid = true;

    const validateField = (fieldName, value, length) => {
      if (!numberRegex.test(value) || value.length !== length) {
        setErrorMessages((prevErrors) => ({ ...prevErrors, [fieldName]: `Please enter a valid ${length}-digit number.` }));
        isValid = false;
      } else {
        setErrorMessages((prevErrors) => ({ ...prevErrors, [fieldName]: '' }));
      }
    };

    validateField('currentMarketValue', currentMarketValue, 1);
    validateField('carpetArea', carpetArea, 1);
    validateField('pincode', pincode, 6);
    validateField('buildingAge', buildingAge, 1);

    return isValid;
  };

  const calculateAdditionalCost = (buildingAge) => {
    if (buildingAge >= 0 && buildingAge <= 5) {
      return 100;
    } else if (buildingAge > 5 && buildingAge <= 10) {
      return 200;
    } else if (buildingAge > 10 && buildingAge <= 15) {
      return 300;
    } else if (buildingAge > 15 && buildingAge <= 20) {
      return 400;
    } else if (buildingAge > 20 && buildingAge <= 25) {
      return 500;
    } else if (buildingAge > 25 && buildingAge <= 30) {
      return 600;
    }

    return 0;
  };

  const clearFormInputs = () => {
    setCurrentMarketValue('');
    setCarpetArea('');
    setPincode('');
    setBuildingAge('0');
    setPropertyEffect('No');
    setSecurity24x7('No');
    setIsSalaried('No');
  };

  return (
    <div className="form-container">
      <h2>Structure Details</h2>
      <form>
        <div className="form-group">
          <label>Current Market Value of Property:</label>
          <input
            type="text"
            className="form-control"
            value={currentMarketValue}
            onChange={(e) => setCurrentMarketValue(e.target.value)}
          />
          <p className="error-message">{errorMessages.currentMarketValue}</p>
        </div>

        <div className="form-group">
          <label>Carpet Area/sq.ft:</label>
          <input
            type="text"
            className="form-control"
            value={carpetArea}
            onChange={(e) => setCarpetArea(e.target.value)}
          />
          <p className="error-message">{errorMessages.carpetArea}</p>
        </div>

        <div className="form-group">
          <label>Enter Pincode:</label>
          <input
            type="text"
            className="form-control"
            value={pincode}
            onChange={(e) => setPincode(e.target.value)}
          />
          <p className="error-message">{errorMessages.pincode}</p>
        </div>

        <div className="form-group">
          <label>Age of Building:</label>
          <select
            className="form-control"
            value={buildingAge}
            onChange={(e) => setBuildingAge(e.target.value)}
          >
            {[0, 5, 10, 15, 20, 25, 30].map((years) => (
              <option key={years} value={years}>
                {`${years}-${years + 5} years`}
              </option>
            ))}
          </select>
          <p className="error-message">{errorMessages.buildingAge}</p>
        </div>

        <div className="form-group">
          <label>Has your property been affected by floods/earthquake in the past 5 years:</label>
          <select
            className="form-control"
            value={propertyEffect}
            onChange={(e) => setPropertyEffect(e.target.value)}
          >
            <option value="Yes">Yes</option>
            <option value="No">No</option>
          </select>
        </div>
      </form>

      <h2>Security Measures</h2>
      <form>
        <div className="form-group">
          <label>24*7 Security:</label>
          <select
            className="form-control"
            value={security24x7}
            onChange={(e) => setSecurity24x7(e.target.value)}
          >
            <option value="Yes">Yes</option>
            <option value="No">No</option>
          </select>
        </div>

        <div className="form-group">
          <label>Are you a salaried individual?:</label>
          <select
            className="form-control"
            value={isSalaried}
            onChange={(e) => setIsSalaried(e.target.value)}
          >
            <option value="Yes">Yes</option>
            <option value="No">No</option>
          </select>
        </div>
      </form>

      <button className="btn btn-primary" onClick={handleProceed}>
        Calculate
      </button>
      <Link to="/formPage" className="btn btn-primary proceed-link ml-4">
        Proceed to Payment
      </Link>

      <button className="btn btn-success ml-4" onClick={handleCalculateSum}>
        Calculate Total Sum
      </button>
      
      <h2>Total Sum</h2>
      <p>{`Total Sum: ${totalSum}`}</p>
    </div>
  );
}

export default PropertyForm;
