// TotalInfo.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useUserContext } from './UserContext';
import { usePropertyContext } from '../InsuranceForms/PropertyContext';

import './TotalInfo.css'

function TotalInfo() {
  const { userData } = useUserContext();
  const navigate = useNavigate();
  const { propertyData } = usePropertyContext();
  const [PanCard, setPanCard] = useState('');
  const [DateofBirth, setDateofBirth] = useState('');
  const [FlatNo, setFlatNo] = useState('');
  const [AreaStreet, setAreaStreet] = useState('');
  const [IsCurrentAddress, setIsCurrentAddress] = useState('');
  const [CurrentAddress, setCurrentAddress] = useState('');
  const [errorMessages, setErrorMessages] = useState({
    PanCard: '',
    DateofBirth: '',
    FlatNo: '',
    AreaStreet: '',
    CurrentAddress: '',
  });

  const validateField = (fieldName, value, regex, errorMessage) => {
    if (!value || (regex && !regex.test(value))) {
      setErrorMessages((prevErrors) => ({
        ...prevErrors,
        [fieldName]: errorMessage,
      }));
      return false;
    } else {
      setErrorMessages((prevErrors) => ({ ...prevErrors, [fieldName]: '' }));
      return true;
    }
  };

  const validateInputs = () => {
    const panCardRegex = /^[A-Z]{5}[0-9]{4}[A-Z]$/;
    const dateOfBirthRegex = /^\d{1,2}\/\d{1,2}\/\d{4}$/;
    const flatNoRegex = /^[A-Za-z0-9\s]+$/;
    const areaStreetRegex = /^[A-Za-z0-9\s]+$/;

    let isValid = true;

    isValid = isValid && validateField('PanCard', PanCard, panCardRegex, 'Please enter a valid PanCard.');
    isValid = isValid && validateField('DateofBirth', DateofBirth, dateOfBirthRegex, 'Please enter a valid date of birth.');
    isValid = isValid && validateField('FlatNo', FlatNo, flatNoRegex, 'Please enter a valid flat number.');
    isValid = isValid && validateField('AreaStreet', AreaStreet, areaStreetRegex, 'Please enter a valid area or street.');

    if (IsCurrentAddress === 'no') {
      isValid = isValid && validateField('CurrentAddress', CurrentAddress, areaStreetRegex, 'Please enter a valid current address.');
    }

    return isValid;
  };

  const handleInputChange = (fieldName, value, regex, errorMessage) => {
    switch (fieldName) {
      case 'PanCard':
        setPanCard(value);
        break;
      case 'DateofBirth':
        setDateofBirth(value);
        break;
      case 'FlatNo':
        setFlatNo(value);
        break;
      case 'AreaStreet':
        setAreaStreet(value);
        break;
      case 'CurrentAddress':
        setCurrentAddress(value);
        break;

      default:
        break;
    }

    validateField(fieldName, value, regex, errorMessage);
  };

  const handleProceed = () => {
    const isValid = validateInputs();

    if (isValid) {
      console.log('Proceeding to the next step...');
      // You can access user data from context here: userData.FullName, userData.Mobile, userData.Email
      navigate('/success');
    } else {
      console.log('Form submission failed. Please check the fields.');
    }
  };

  return (
    <div className='org12'>
      <h2>Fill the details</h2>
      <div className='forDispFlex'> 
        <div className='fortablecls'>
        <h3>user Details</h3>
        <table className='fortable container '>
          <tr>
            <th></th>
            <th></th>
            <th></th>
          </tr>
          <tr>
            <td className='tbfont'>FullName:</td>
            <td><select>
            <option>Mr.</option>
            <option>Mrs.</option>
            <option>Ms.</option>
            </select></td>
            <td className='tbfont1'>{userData.FullName}</td>
          </tr>
          <tr>
            <td className='tbfont'>MobileNo</td>
            <td>:</td>
            <td className='tbfont1'>{userData.Mobile}</td>
          </tr>
          <tr>
            <td className='tbfont'>Email.Id</td>
            <td>:</td>
            <td className='tbfont1'>{userData.Email}</td>
          </tr>
        </table>
        <div><h3>Property Details</h3>
          {/* Display property details from the context */}
          <p>Current Market Value: {propertyData.currentMarketValue}</p>
          <p>Carpet Area: {propertyData.carpetArea}</p>
          <p>Pincode: {propertyData.pincode}</p>
          {/* ... (display other details) */}</div>
        </div>
        <div>
      <h2>Total Information</h2>
      {/* Display propertyData and popupMessage as needed */}
      <pre>{JSON.stringify(propertyData, null, 2)}</pre>
      <h3>{popupMessage}</h3>
      {/* ... (any other content you want to display) */}
    </div>
        <div className='forinner11d'>
        <form className='forinner11 container'>
        
          <h3>Enter PanCard details</h3>
          <div className='form-group11 container'>
            <div>PanCard:</div><span>
            <div className='form-group11Input container'>
              <input
                type='text'
                className=''
                placeholder='ex: ABCDE1234F'
                value={PanCard}
                onChange={(e) =>
                  handleInputChange('PanCard', e.target.value, /^[A-Z]{5}[0-9]{4}[A-Z]$/, 'Please enter a valid PanCard.')
                }
              />
              <p className='error-message11' style={{ color: 'red', textAlign:'center' }}>{errorMessages.PanCard}</p>
            </div></span>
          </div>

          <div className='form-group11 container '>
            <div>DateofBirth:</div>
            <div className='form-group11Input container'>
              <input
                type='text'
                className=''
                placeholder='ex: dd/mm/yyyy'
                value={DateofBirth}
                onChange={(e) => handleInputChange('DateofBirth', e.target.value, /^\d{1,2}\/\d{1,2}\/\d{4}$/, 'Please enter a valid date of birth.')}
              />
              <p className='error-message11' style={{ color: 'red', textAlign:'center' }}>{errorMessages.DateofBirth}</p>
            </div>
          </div>
          <h3>property details</h3>

          <div className='form-group11 container '>
            <div>FlatNo/House No:</div>
            <div className='form-group11Input container'>
              <input
                type='text'
                className=''
                placeholder='ex: 123'
                value={FlatNo}
                onChange={(e) => handleInputChange('FlatNo', e.target.value, /^[A-Za-z0-9\s]+$/, 'Please enter a valid flat number.')}
              />
              <p className='error-message11' style={{ color: 'red', textAlign:'center' }}>{errorMessages.FlatNo}</p>
            </div>
          </div>

          <div className='form-group11 container '>
            <div>Area/Street:</div>
            <div className='form-group11Input container'>
              <input
                type='text'
                className=''
                placeholder='ex: Main Street'
                value={AreaStreet}
                onChange={(e) => handleInputChange('AreaStreet', e.target.value, /^[A-Za-z0-9\s]+$/, 'Please enter a valid area or street.')}
              />
              <p className='error-message11' style={{ color: 'red', textAlign:'center' }}>{errorMessages.AreaStreet}</p>
            </div>
          </div>

          <div className='form-group11 container '>
            <div>Is the address mentioned above your current address:</div>
            <div className='form-group11Input container'>
              <select onChange={(e) => setIsCurrentAddress(e.target.value)}>
                <option value=''>Select</option>
                <option value='yes'>Yes</option>
                <option value='no'>No</option>
              </select>
            </div>
          </div>

          {IsCurrentAddress === 'no' && (
            <div className='form-group11 container '>
              <div>Current Address:</div>
              <div className='form-group11Input container'>
                <input
                  type='text'
                  className=''
                  placeholder='ex: Current Address'
                  value={CurrentAddress}
                  onChange={(e) =>
                    handleInputChange('CurrentAddress', e.target.value, /^[A-Za-z0-9\s]+$/, 'Please enter a valid current address.')
                  }
                />
                <p className='error-message11' style={{ color: 'red', textAlign:'center' }}>{errorMessages.CurrentAddress}</p>
              </div>
            </div>
          )}
          <button className='btn btn-success my-2 ' type='button' onClick={handleProceed}>
            Proceed
          </button>
        </form>
      </div>
      <div></div>
      </div>
    </div>
  );
}

export default TotalInfo;
