// PopupModal.js

import React,{ useState } from 'react';
import '../loginORSign/LoginSigin.css';
import SignInForm from '../loginORSign/SignInForm';
import LoginForm from '../loginORSign/LoginForm';

const PopupModal = ({ message, onClose }) => {
  const [isSignIn, setIsSignIn] = useState(true);

  const toggleView = () => {
    setIsSignIn((prev) => !prev);
  };
  return (
    <div className="popup-modal">
      <div className="popup-content">
        <h3 className='forMsg'>{message}</h3>
        <div className="container loginForm">
      <div className="box ">
        <h2>{isSignIn ? 'Sign In' : 'Login'}</h2>
        {isSignIn ? <SignInForm /> : <LoginForm />}
        <p onClick={toggleView} className="toggle-link">
          {isSignIn ? 'Switch to Login' : 'Switch to Sign In'}
        </p>
      </div>
    </div>
        <button  className='btn btn-danger' onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default PopupModal;
