// TotalInfo.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useUserContext } from './UserContext';

function TotalInfo() {
  const { userData } = useUserContext();
  const navigate = useNavigate();

  const [PanCard, setPanCard] = useState('');
  const [DateofBirth, setDateofBirth] = useState('');
  const [FlatNo, setFlatNo] = useState('');
  const [AreaStreet, setAreaStreet] = useState('');
  const [IsCurrentAddress, setIsCurrentAddress] = useState('');
  const [CurrentAddress, setCurrentAddress] = useState('');

  const [errorMessages, setErrorMessages] = useState({
    PanCard: '',
    DateofBirth: '',
    FlatNo: '',
    AreaStreet: '',
    CurrentAddress: '',
  });

  const validateField = (fieldName, value, regex, errorMessage) => {
    if (!value || (regex && !regex.test(value))) {
      setErrorMessages((prevErrors) => ({
        ...prevErrors,
        [fieldName]: errorMessage,
      }));
      return false;
    } else {
      setErrorMessages((prevErrors) => ({ ...prevErrors, [fieldName]: '' }));
      return true;
    }
  };

  const validateInputs = () => {
    const panCardRegex = /^[A-Z]{5}[0-9]{4}[A-Z]$/;
    const dateOfBirthRegex = /^\d{1,2}\/\d{1,2}\/\d{4}$/;
    const flatNoRegex = /^[A-Za-z0-9\s]+$/;
    const areaStreetRegex = /^[A-Za-z0-9\s]+$/;

    let isValid = true;

    isValid = isValid && validateField('PanCard', PanCard, panCardRegex, 'Please enter a valid PanCard.');
    isValid = isValid && validateField('DateofBirth', DateofBirth, dateOfBirthRegex, 'Please enter a valid date of birth.');
    isValid = isValid && validateField('FlatNo', FlatNo, flatNoRegex, 'Please enter a valid flat number.');
    isValid = isValid && validateField('AreaStreet', AreaStreet, areaStreetRegex, 'Please enter a valid area or street.');

    if (IsCurrentAddress === 'no') {
      isValid = isValid && validateField('CurrentAddress', CurrentAddress, areaStreetRegex, 'Please enter a valid current address.');
    }

    return isValid;
  };

  const handleProceed = () => {
    const isValid = validateInputs();

    if (isValid) {
      console.log('Proceeding to the next step...');
      // You can access user data from context here: userData.FullName, userData.Mobile, userData.Email
      navigate('/success');
    } else {
      console.log('Form submission failed. Please check the fields.');
    }
  };
  return (
    <div className='org1'>
      <div>
        <h2>User Details:</h2>
        <p>Full Name: {userData.FullName}</p>
        <p>Mobile: {userData.Mobile}</p>
        <p>Email: {userData.Email}</p>
      </div>
      <div>
        <h2>Enter PanCard details :</h2>
        <form className='forinner1 container'>
          <div className='form-group1 container'>
            <div>PanCard:</div>
            <div className='form-group1Input container'>
              <input
                type='text'
                className=''
                placeholder='ex: QW98655A'
                value={PanCard}
                onChange={(e) => setPanCard(e.target.value)}
              />
              <p className='error-message1'>{errorMessages.PanCard}</p>
            </div>
          </div>

          <div className='form-group1 container '>
            <div>DateofBirth:</div>
            <div className='form-group1Input container'>
              <input
                type='text'
                className=''
                placeholder='ex: dd/mm/yyyy'
                value={DateofBirth}
                onChange={(e) => setDateofBirth(e.target.value)}
              />
              <p className='error-message1'>{errorMessages.DateofBirth}</p>
            </div>
          </div>

          <div className='form-group1 container '>
            <div>FlatNo/House No:</div>
            <div className='form-group1Input container'>
              <input
                type='text'
                className=''
                placeholder='ex: 123'
                value={FlatNo}
                onChange={(e) => setFlatNo(e.target.value)}
              />
              <p className='error-message1'>{errorMessages.FlatNo}</p>
            </div>
          </div>

          <div className='form-group1 container '>
            <div>Area/Street:</div>
            <div className='form-group1Input container'>
              <input
                type='text'
                className=''
                placeholder='ex: Main Street'
                value={AreaStreet}
                onChange={(e) => setAreaStreet(e.target.value)}
              />
              <p className='error-message1'>{errorMessages.AreaStreet}</p>
            </div>
          </div>

          <div className='form-group1 container '>
            <div>Is the address mentioned above your current address:</div>
            <div className='form-group1Input container'>
              <select onChange={(e) => setIsCurrentAddress(e.target.value)}>
                <option value=''>Select</option>
                <option value='yes'>Yes</option>
                <option value='no'>No</option>
              </select>
            </div>
          </div>

          {IsCurrentAddress === 'no' && (
            <div className='form-group1 container '>
              <div>Current Address:</div>
              <div className='form-group1Input container'>
                <input
                  type='text'
                  className=''
                  placeholder='ex: Current Address'
                  value={CurrentAddress}
                  onChange={(e) => setCurrentAddress(e.target.value)}
                />
                <p className='error-message1'>{errorMessages.CurrentAddress}</p>
              </div>
            </div>
          )}

          <button className='btn btn-success my-2' type='button' onClick={handleProceed}>
            Proceed
          </button>
        </form>
      </div>
    </div>
  );
}

export default TotalInfo;
