import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function SignInPage() {
  const [FullName, setFullName] = useState('');
  const [Mobile, setMobile] = useState('');
  const [Email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessages, setErrorMessages] = useState({
    FullName: '',
    Mobile: '',
    Email: '',
    password: '',
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    const isValid = validateInputs();

    if (isValid) {
      console.log('Form submitted:', {
        FullName,
        Mobile,
        Email,
        password,
      });

      // No need to programmatically navigate, Link will handle it
    } else {
      console.log('Form submission failed. Please check the fields.');
    }
  };

  const validateInputs = () => {
    const nameRegex = /^[A-Za-z. ]{3,}$/;
    const mobileRegex = /^[9876][0-9]{9}$/;
    const emailRegex = /^[^\s@]+@[^\s@]+\.(com|in)$/;
    const passwordRegex = /^[A-Za-z.0-9!@#$%^&*]{8,}$/;

    let isValid = true;

    const validateField = (fieldName, value, regex, errorMessage) => {
      if (!value || (regex && !regex.test(value))) {
        setErrorMessages((prevErrors) => ({
          ...prevErrors,
          [fieldName]: errorMessage,
        }));
        isValid = false;
      } else {
        setErrorMessages((prevErrors) => ({ ...prevErrors, [fieldName]: '' }));
      }
    };

    validateField('FullName', FullName, nameRegex, 'Please enter a valid full name.');
    validateField('Mobile', Mobile, mobileRegex, 'Please enter a valid 10-digit mobile number.');
    validateField('Email', Email, emailRegex, 'Please enter a valid email address.');
    validateField(
      'password',
      password,
      passwordRegex,
      'Password must be at least 8 characters, contain at least one letter, one number, and one special character.'
    );

    return isValid;
  };

  return (
    <div className='org'>
      <h2>SignIn forher</h2>
      <form className='forinner container' onSubmit={handleSubmit}>
        {/* Your form fields go here */}

        <button className='btn btn-success my-2' type='submit'>
          Sign In
        </button>

        {/* Link to the success page */}
        <p>
          Already have an account? <Link to='/success'>Go to Success Page</Link>
        </p>
      </form>
    </div>
  );
}

export default SignInPage;
