import React, { useState } from 'react';

function TotalInfo() {
  const [PanCard, setPanCard] = useState('');
  const [FullName, setFullName] = useState('');
  const [Email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [DateofBirth, setDateofBirth] = useState('');
  const [errorMessages, setErrorMessages] = useState({
    PanCard:"",
    FullName: '',
    Mobile: '',
    Email: '',
    password: '',
  });

  // Function to validate individual field
  const validateField = (fieldName, value, regex, errorMessage) => {
    if (!value || (regex && !regex.test(value))) {
      setErrorMessages((prevErrors) => ({
        ...prevErrors,
        [fieldName]: errorMessage,
      }));
      return false;
    } else {
      setErrorMessages((prevErrors) => ({ ...prevErrors, [fieldName]: '' }));
      return true;
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const isValid = validateInputs();

    if (isValid) {
      console.log('Form submitted:', {
        PanCard,
        FullName,
        Mobile,
        Email,
        password,
      });
      // Simulate navigation to the success page
      window.location.href = '/success';
    } else {
      console.log('Form submission failed. Please check the fields.');
    }
  };

  const validateInputs = () => {
    const PanCardRegex= /^[A-Z]{5}[0-9]{4}[A-Z]$/;
    const nameRegex = /^[A-Za-z][A-Za-z. ]{1,18}[A-Za-z. ]$/;
    const mobileRegex = /^[A-Za-z][A-Za-z. ]{1,18}[A-Za-z. ]$/;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const passwordRegex = /^(?=.*[A-Za-z])(?=.*\d)(?=.*[@$!%*#?&])[A-Za-z\d@$!%*#?&]{8,}$/;

    let isValid = true;

    isValid = isValid && validateField('PanCard', PanCard, PanCardRegex, 'Please enter a valid full name.');
    isValid = isValid && validateField('FullName', FullName, nameRegex, 'Please enter a valid full name.');
    isValid = isValid && validateField('Mobile', Mobile, mobileRegex, 'Please enter a valid 10-digit mobile number.');
    isValid = isValid && validateField('Email', Email, emailRegex, 'Please enter a valid email address.');
    isValid = isValid && validateField(
      'password',
      password,
      passwordRegex,
      'Password must be at least 8 characters, contain at least one letter, one number, and one special character.'
    );

    return isValid;
  };

  const handleInputChange = (fieldName, value, regex, errorMessage) => {
    setFullName(fieldName === 'PanCard' ? value : PanCard);
    setFullName(fieldName === 'FullName' ? value : FullName);
    setMobile(fieldName === 'Mobile' ? value.slice(0, 10) : Mobile);
    setEmail(fieldName === 'Email' ? value : Email);
    setPassword(fieldName === 'password' ? value : password);

    validateField(fieldName, value, regex, errorMessage);
  };

  return (
    <div className='org1'>
      <h2>Enter PanCard details :</h2>
      <form className='forinner1 container' onSubmit={handleSubmit}>
        <div className='form-group1 container '><div>
        PanCard:{/*  <span style={{ color: 'red'}}>*</span> : */}
          </div><div className='form-group1Input container'><input
            type='text'
            className=''
            placeholder='ex: QW98655A'
            value={PanCard}
            onChange={(e) => handleInputChange('PanCard', e.target.value, /^[A-Z]{5}[0-9]{4}[A-Z]$/, 'Please enter a valid full name.')}
          />
          <p className='error-message1'>{errorMessages.FullName}</p>
        </div>
        </div>

        <div className='form-group1 container '><div>
        FullName:{/*  <span style={{ color: 'red'}}>*</span> : */}
          </div><div className='form-group1Input container'><input
            type='text'
            className=''
            placeholder='ex: Ramana'
            value={FullName}
            onChange={(e) => handleInputChange('FullName', e.target.value, /^[A-Za-z][A-Za-z. ]{1,18}[A-Za-z. ]$/, 'Please enter a valid 10-digit mobile number.')}
          />
          <p className='error-message1'>{errorMessages.Mobile}</p>
        </div>
        </div>

        <div className='form-group1 container '><div>
        EmailId:{/*  <span style={{ color: 'red'}}>*</span> : */}
          </div><div className='form-group1Input container'><input
            type='text'
            className=''
            placeholder='ex: example@gmail.com'
            value={Email}
            onChange={(e) => handleInputChange('Email', e.target.value, /^[^\s@]+@[^\s@]+\.[^\s@]+$/, 'Please enter a valid email address.')}
          />
          <p className='error-message1'>{errorMessages.Email}</p>
        </div>
        </div>

        <div className='form-group1 container '><div>
        DateofBirth:{/*  <span style={{ color: 'red'}}>*</span> : */}
          </div><div className='form-group1Input container'><input
            type='date'
            className=''
            placeholder='ex: 01/03/2024'
            value={Email}
            onChange={(e) => handleInputChange('Email', e.target.value, /^[^\s@]+@[^\s@]+\.[^\s@]+$/, 'Please enter a valid email address.')}
          />
          <p className='error-message1'>{errorMessages.Email}</p>
        </div>
        </div>

        <button className='btn btn-success my-2' type='submit'>
          next
        </button>
      </form>
    </div>
  );
}

export default TotalInfo;
