// PropertyContext.js

import React, { createContext, useContext, useState } from 'react';

const PropertyContext = createContext();

export const PropertyProvider = ({ children }) => {
  const [propertyData, setPropertyData] = useState({});
  const [popupMessage, setPopupMessage] = useState('');
  const [propertyValue, setPropertyValue] = useState(null);
  const [buildingAge, setBuildingAge] = useState(null);
  const [carpetArea, setCarpetArea] = useState(null);
  const [pincode, setPincode] = useState(null);

  const updatePropertyData = (data) => {
    setPropertyData(data);
    setPropertyValue(data.propertyValue);
    setBuildingAge(data.buildingAge);
    setCarpetArea(data.carpetArea);
    setPincode(data.carpetArea);
  };

  const updatePopupMessage = (message) => {
    setPopupMessage(message);
  };

  return (
    <PropertyContext.Provider
      value={{ propertyData, updatePropertyData, popupMessage, updatePopupMessage, propertyValue, buildingAge, carpetArea, pincode}}
    >
      {children}
    </PropertyContext.Provider>
  );
};

export const usePropertyContext = () => {
  return useContext(PropertyContext);
};
