import React, { useEffect } from 'react';

import ramanaSoft from '../components/Images/ramanaSoft.jpg';
import { useNavigate, useLocation } from 'react-router-dom';

const PaymentComponent = () => {
  // Use useLocation to get the location object
  const navigate = useNavigate();
  const location = useLocation();  const data = location.state; // This is the data object sent from TotalInfo

  useEffect(() => {
    // Load Razorpay script dynamically
    const script = document.createElement('script');
    script.src = 'https://checkout.razorpay.com/v1/checkout.js';
    script.async = true;
    script.onload = initializeRazorpay;

    document.head.appendChild(script);

    // Clean up script tag on component unmount
    return () => {
      document.head.removeChild(script);
    };
  }, []);

  const initializeRazorpay = () => {
    const options = {
      key: 'rzp_test_Su4WV4zdBIGTmZ',
      key_secret: 'EmH6eToe5CvCfAfgfADREv3C',
      amount: 10000,
      name: 'Ramana Insurance',
      description: 'Product/Service Description',
      image: ramanaSoft,
      handler: function (response) {
        alert(`Razorpay Payment ID: ${response.razorpay_payment_id}`);
        navigate('/'); // Replace '/success-page' with the desired route for success
      },
      prefill: {
        name: "Ashu", // Use fullName from data object
        email: 'your_email@example.com', // You can use data object properties
        contact: 'Your Contact Number',
      },
      notes: {
        address: 'Your Address',
      },
      theme: {
        color: '#F37254',
      },
    };

    const pay = new window.Razorpay(options);
    pay.open();
  };

  return (
    <div>
      <button onClick={initializeRazorpay}>Pay with Razorpay</button>
    </div>
  );
};

export default PaymentComponent;
