// PropertyForm.js

import React, { useState } from 'react';
import './PropertyForm.css';
import { Link, useHistory } from 'react-router-dom';

function PropertyForm() {
  const history = useHistory();
  const [currentMarketValue, setCurrentMarketValue] = useState('');
  const [carpetArea, setCarpetArea] = useState('');
  const [pincode, setPincode] = useState('');
  const [buildingAge, setBuildingAge] = useState('0');
  const [duration, setDuration] = useState('0');
  const [propertyEffect, setPropertyEffect] = useState('No');
  const [security24x7, setSecurity24x7] = useState('No');
  const [isSalaried, setIsSalaried] = useState('No');
  const [errorMessages, setErrorMessages] = useState({
    currentMarketValue: '',
    carpetArea: '',
    pincode: '',
    buildingAge: '',
    duration: '',
  });
  const [totalSum, setTotalSum] = useState(0);

  const handleCombined = () => {
    handleProceed();
    handleCalculateSum();

    // Redirect to the next page along with the totalSum
    history.push(`/nextPage?totalSum=${totalSum}`);
  };

  const handleProceed = () => {
    const isValid = validateInputs();

    if (isValid) {
      const propertyValue = parseInt(currentMarketValue, 10);
      const areaValue = parseInt(carpetArea, 10);
      const pincodeValue = parseInt(pincode, 10);
      const totalPropertyValue = propertyValue * 0.01;
      const forsecurity24x7 = propertyValue * 0.01;
      const propertyEffectValue = forsecurity24x7 + propertyValue * 0.01;

      const formData = {
        currentMarketValue: totalPropertyValue,
        carpetArea: areaValue,
        pincode: pincodeValue,
        propertyEffect,
        forsecurity24x7,
        isSalaried,
      };

      console.log('Form submitted:', formData);
    } else {
      console.log('Form submission failed. Please enter valid numbers.');
    }
  };

  const handleCalculateSum = () => {
    const propertyValue = parseInt(currentMarketValue, 10);
    const totalPropertyValue = propertyValue * 0.01;
    const forsecurity24x7 = propertyValue * 0.008;
    const propertyEffectValue = propertyValue * 0.007;

    const sum =
      (duration === "1" ? totalPropertyValue * 0.9 : 0) +
      (duration === "2" ? totalPropertyValue * 0.8 : 0) +
      (duration === "3" ? totalPropertyValue * 0.7 : 0) +
      (duration === "4" ? totalPropertyValue * 0.6 : 0) +
      (duration === "5" ? totalPropertyValue * 0.5 : 0) +

      (buildingAge >= 0 && buildingAge <= 5 ? totalPropertyValue * 0.09 : 0) +
      (buildingAge > 5 && buildingAge <= 10 ? totalPropertyValue * 0.08 : 0) +
      (buildingAge > 10 && buildingAge <= 15 ? totalPropertyValue * 0.07 : 0) +
      (buildingAge > 15 && buildingAge <= 20 ? totalPropertyValue * 0.06 : 0) +
      (buildingAge > 20 && buildingAge <= 25 ? totalPropertyValue * 0.05 : 0) +
      (buildingAge > 25 && buildingAge <= 30 ? totalPropertyValue * 0.04 : 0) +

      (propertyEffect === 'Yes' ? 0 : propertyEffectValue) +
      (security24x7 === 'Yes' ? 0 : forsecurity24x7) +
      (isSalaried === 'Yes' ? 100 : 0);

    setTotalSum(sum);

    if (propertyEffect === 'Yes') {
      alert('Due to being affected by floods/earthquake in the past 5 years.');
    }

    if (isSalaried === 'No') {
      alert('Not a salaried individual.');
    }

    console.log(`Total Sum: ${sum}`);
  };

  const validateInputs = () => {
    const numberRegex = /^\d+$/;
    let isValid = true;

    const validateField = (fieldName, value, length) => {
      const fieldElement = document.getElementById(fieldName);

      if (!fieldElement || (!numberRegex.test(value) || (length && value.length !== length))) {
        setErrorMessages((prevErrors) => ({
          ...prevErrors,
          [fieldName]: length
            ? `Please enter a valid ${length}-digit number.`
            : 'Please enter a valid number.',
        }));
        isValid = false;

        if (fieldElement) {
          fieldElement.classList.add('error-input');
        }
      } else {
        setErrorMessages((prevErrors) => ({ ...prevErrors, [fieldName]: '' }));

        if (fieldElement) {
          fieldElement.classList.remove('error-input');
        }
      }
    };

    validateField('currentMarketValue', currentMarketValue);
    validateField('carpetArea', carpetArea);
    validateField('pincode', pincode, 6);
    validateField('buildingAge', buildingAge, 1);
    validateField('duration', duration, 1);

    return isValid;
  };

  return (
    <div className='bg'>
      <h2>Prediction for the Premium Calculation</h2>
      <div className='form-container container'>
        <h2>Structure Details</h2>
        <form className='forinner container'>
          <div className='form-group'>
            <label>Current Market Value of Property:</label>
            <input
              type='text'
              id='currentMarketValue'
              className={`form-control ${errorMessages.currentMarketValue ? 'error-messages' : ''}`}
              placeholder='ex : 012345'
              value={currentMarketValue}
              onChange={(e) => setCurrentMarketValue(e.target.value)}
            />
            <p className='error-message'>{errorMessages.currentMarketValue}</p>
          </div>

          <div className='form-group'>
            <label>Carpet Area/sq.ft:</label>
            <input
              type='text'
              className='form-control'
              placeholder='ex : 1200'
              value={carpetArea}
              onChange={(e) => setCarpetArea(e.target.value)}
            />
            <p className='error-message'>{errorMessages.carpetArea}</p>
          </div>

          <div className='form-group'>
            <label>Enter Pincode:</label>
            <input
              type='text'
              className='form-control'
              placeholder='ex : 515001'
              value={pincode}
              onChange={(e) => setPincode(e.target.value)}
            />
            <p className='error-message'>{errorMessages.pincode}</p>
          </div>

          <div className='form-group'>
            <label>Age of Building:</label>
            <select
              className='form-control form-select'
              value={buildingAge}
              onChange={(e) => setBuildingAge(e.target.value)}
            >
              {[0, 5, 10, 15, 20, 25, 30].map((years) => (
                <option key={years} value={years}>
                  {`${years}-${years + 5} years`}
                </option>
              ))}
            </select>
          </div>

          <div className='form-group'>
            <label>Has your property been affected by floods/earthquake in the past 5 years:</label>
            <select
              className='form-control form-select'
              value={propertyEffect}
              onChange={(e) => setPropertyEffect(e.target.value)}
            >
              <option value='Yes'>Yes</option>
              <option value='No'>No</option>
            </select>
            <div className='form-group'>
              <label>Premium Duration:</label>
              <select
                className='form-control form-select'
                value={duration}
                onChange={(e) => setDuration(e.target.value)}
              >
                <option value='1'>1 year</option>
                <option value='2'>2 years</option>
                <option value='3'>3 years</option>
                <option value='4'>4 years</option>
                <option value='5'>5 years</option>
              </select>
            </div>
          </div>
        </form>

        <h2>Security Measures</h2>
        <form className='forinner container'>
          <div className='form-group'>
            <label>24*7 Security:</label>
            <select
              className='form-control form-select'
              value={security24x7}
              onChange={(e) => setSecurity24x7(e.target.value)}
            >
              <option value='Yes'>Yes</option>
              <option value='No'>No</option>
            </select>
          </div>

          <div className='form-group'>
            <label>Are you a salaried ?:</label>
            <select
              className='form-control form-select'
              value={isSalaried}
              onChange={(e) => setIsSalaried(e.target.value)}
            >
              <option value='Yes'>Yes</option>
              <option value='No'>No</option>
            </select>
          </div>
        </form>
        <h2 className='premium-price '>{`Premium Price: ${totalSum}`}</h2>
        <button className='btn btn-success' onClick={handleCombined}>
          Calculate
        </button>
        <Link to='/formPage' className='btn btn-primary proceed-link ml-4'>
          Proceed to Payment
        </Link>
      </div>
    </div>
  );
}

export default PropertyForm;
