// Payment.jsx
import React, { useState } from 'react';
import Razorpay from 'razorpay';

const Payment = () => {
  const [payment, setPayment] = useState(null);

  const options = {
    key: 'YOUR_RAZORPAY_KEY',
    amount: 10000, // amount in paise (100 paise = 1 INR)
    currency: 'INR',
    name: 'Your Company Name',
    description: 'Payment for your product/service',
    image: 'your_logo_url',
    order_id: payment ? payment.id : '',
    handler: (response) => {
      alert(response.razorpay_payment_id);
      alert(response.razorpay_order_id);
      alert(response.razorpay_signature);
    },
    prefill: {
      name: 'John Doe',
      email: 'john@example.com',
      contact: '9876543210',
    },
    notes: {
      address: 'Your Company Address',
    },
    theme: {
      color: '#3399cc',
    },
  };

  const openRazorpay = () => {
    const razorpayInstance = new Razorpay(options);
    razorpayInstance.open();
  };

  const createOrder = async () => {
    // Make an API call to your server to create a Razorpay order
    const response = await fetch('YOUR_SERVER_API_ENDPOINT', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        amount: options.amount,
        currency: options.currency,
      }),
    });

    const orderData = await response.json();
    setPayment(orderData);

    // Once the order is created, open the Razorpay payment modal
    openRazorpay();
  };

  return (
    <div>
      <button onClick={createOrder}>Pay with Razorpay</button>
    </div>
  );
};

export default Payment;
