import React, { useState } from 'react';

const Calculator = () => {
  const [formData, setFormData] = useState({
    currentMarketValue: 0,
    carpetArea: 0,
    pincode: '',
    ageOfBuilding: '',
    affectedByDisaster: 'No',
    security24x7: 'No',
    isSalaried: 'No',
  });

  const [totalSum, setTotalSum] = useState(0);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const calculateSum = () => {
    let sum = 0;

    sum += 0.01 * formData.currentMarketValue;
    sum += formData.currentMarketValue;
    sum += formData.carpetArea;

    switch (formData.ageOfBuilding) {
      case '0-5years':
        sum += 100;
        break;
      case '5-10years':
        sum += 200;
        break;
      case '10-15years':
        sum += 300;
        break;
      case '15-20years':
        sum += 400;
        break;
      case '20-25years':
        sum += 500;
        break;
      default:
        break;
    }

    if (formData.affectedByDisaster === 'Yes') {
      sum += 100;
    }

    if (formData.security24x7 === 'Yes') {
      sum += 100;
    } else {
      sum -= 200;
    }

    if (formData.isSalaried === 'Yes') {
      sum += 150;
    } else {
      sum += 50;
    }

    setTotalSum(sum);

    return sum;
  };

  const handleProceed = () => {
    const totalSum = calculateSum();
    alert(`Sum of details: ${totalSum}`);
    // Add further actions or API calls here
  };

  return (
    <div>
      <div>
        <h2>Structure Details</h2>
        {/* ... (rest of the code remains the same) */}
      </div>

      <div>
        <h2>Security Measures</h2>
        {/* ... (rest of the code remains the same) */}
      </div>

      <button onClick={handleProceed}>Proceed</button>

      <div>
        <h2>Total Sum of Details</h2>
        <p>{totalSum}</p>
      </div>
    </div>
  );
};

export default Calculator;
