import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Import Axios for making HTTP requests
import { useUserContext } from '../ListOfPolicies/UserContext';


function SignInPage() {
  const { updateUserData } = useUserContext();
  const navigate = useNavigate();
  const [FullName, setFullName] = useState('');
  const [Mobile, setMobile] = useState('');
  const [Email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessages, setErrorMessages] = useState({
    FullName: '',
    Mobile: '',
    Email: '',
    password: '',
  });

  // Function to validate field and handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    const isValid = validateInputs();

    if (isValid) {
      console.log('Form submitted:', {
        FullName,
        Mobile,
        Email,
        password,
      });

      // Prepare data object to send to the backend
      const formData = {
        fullName: FullName,
        mobile: Mobile,
        email: Email,
        password: password,
      };

      // Send formData to backend API
      axios.post('http://localhost:9856/api/signin/add', formData)
        .then(response => {
          console.log('Sign in successful:', response.data);
          // Update user data context or perform any additional actions
          updateUserData({ FullName, Mobile, Email });
          // Navigate to the success page
          navigate('/success');
        })
        .catch(error => {
          console.error('Sign in failed:', error);
          // Handle errors, such as displaying an error message to the user
        });
    } else {
      console.log('Form submission failed. Please check the fields.');
    }
  };

  // Function to validate form inputs
  const validateInputs = () => {
    const nameRegex = /^[A-Za-z][A-Za-z. ]{1,18}[A-Za-z. ]$/;
    const mobileRegex = /^[987][0-9]{9}$/;
    const emailRegex = /^[^\s!@#$%^&*@]+@[^\s@]+\.(com|in)$/;
    const passwordRegex = /(?=.*[0-9])(?=.*[@])(?=.*[A-Z])[A-Za-z0-9!@#$%^&*]{8,15}$/;

    let isValid = true;

    const validateField = (fieldName, value, regex, errorMessage) => {
      if (!value || (regex && !regex.test(value))) {
        setErrorMessages((prevErrors) => ({
          ...prevErrors,
          [fieldName]: errorMessage,
        }));
        isValid = false;
      } else {
        setErrorMessages((prevErrors) => ({ ...prevErrors, [fieldName]: '' }));
      }
    };

    validateField('FullName', FullName, nameRegex, 'Please enter a valid full name.');
    validateField('Mobile', Mobile, mobileRegex, 'Please enter a valid 10-digit mobile number.');
    validateField('Email', Email, emailRegex, 'Please enter a valid email address.');
    validateField('password', password, passwordRegex, 'Password must be at least 8 characters, contain at least one letter, one number, and one special character.');

    return isValid;
  };

  return (
    <div className='org1'>
      <form className='forinner1 container' onSubmit={handleSubmit}>
        <div className='form-group1 container '>
          <div>FullName:</div>
          <div className='form-group1Input container'>
            <input
              type='text'
              className=''
              placeholder='ex: FullName'
              value={FullName}
              onChange={(e) => setFullName(e.target.value)}
            />
            <p className='error1-message1' style={{ color: 'red', textAlign:'center'}}>{errorMessages.FullName}</p>
          </div>
        </div>

        <div className='form-group1 container '>
          <div>MobileNo:</div>
          <div className='form-group1Input container'>
            <input
              type='text'
              className=''
              placeholder='ex: 9876543210'
              value={Mobile}
              onChange={(e) => setMobile(e.target.value)}
            />
            <p className='error-message1' style={{ color: 'red', textAlign:'center' }}>{errorMessages.Mobile}</p>
          </div>
        </div>

        <div className='form-group1 container '>
          <div>EmailId:</div>
          <div className='form-group1Input container'>
            <input
              type='text'
              className=''
              placeholder='ex: example@gmail.com'
              value={Email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <p className='error-message1' style={{ color: 'red', textAlign:'center' }}>{errorMessages.Email}</p>
          </div>
        </div>
        
        <div className='form-group1 container '>
          <div>password:</div>
          <div className='form-group1Input container'>
            <input
              type='password'
              className=''
              placeholder='ex: Abc@123'
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <p className='error-message1' style={{ color: 'red', textAlign:'center' }}>{errorMessages.password}</p>
          </div>
        </div>

        <button className='btn btn-success my-2' type='submit'>
          Sign In
        </button>
      </form>
    </div>
  );
}

export default SignInPage;
