import React, { useState } from 'react';

function TotalInfo({ FullName, Mobile, Email }) {
  const [PanCard, setPanCard] = useState('');
  const [errorMessages, setErrorMessages] = useState({
    PanCard: '',
  });

  const validateField = (fieldName, value, regex, errorMessage) => {
    if (!value || (regex && !regex.test(value))) {
      setErrorMessages((prevErrors) => ({
        ...prevErrors,
        [fieldName]: errorMessage,
      }));
      return false;
    } else {
      setErrorMessages((prevErrors) => ({ ...prevErrors, [fieldName]: '' }));
      return true;
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const isValid = validateInputs();

    if (isValid) {
      console.log('Form submitted:', {
        PanCard,
        FullName,
        Email,
        Mobile,
      });
      // Simulate navigation to the success page
      window.location.href = '/success';
    } else {
      console.log('Form submission failed. Please check the fields.');
    }
  };

  const validateInputs = () => {
    const PanCardRegex = /^[A-Z]{5}[0-9]{4}[A-Z]$/;

    let isValid = true;

    isValid = isValid && validateField('PanCard', PanCard, PanCardRegex, 'Please enter a valid PAN card number.');

    return isValid;
  };

  const handleInputChange = (fieldName, value, regex, errorMessage) => {
    setPanCard(fieldName === 'PanCard' ? value : PanCard);

    validateField(fieldName, value, regex, errorMessage);
  };

  return (
    <div className='org1'>
      <h2>Fill the details</h2>
      <form className='forinner1 container' onSubmit={handleSubmit}>
        <div className='form-group1 container'>
          <div>PanCard:</div>
          <div className='form-group1Input container'>
            <input
              type='text'
              className=''
              placeholder='ex: QW98655A'
              value={PanCard}
              onChange={(e) =>
                handleInputChange('PanCard', e.target.value, /^[A-Z]{5}[0-9]{4}[A-Z]$/, 'Please enter a valid PAN card number.')
              }
            />
            <p className='error-message1'>{errorMessages.PanCard}</p>
          </div>
        </div>

        <div className='form-group1 container'>
          <div>FullName:</div>
          <div className='form-group1Input container'>
            <input type='text' className='' value={FullName} disabled />
          </div>
        </div>

        <div className='form-group1 container'>
          <div>Email:</div>
          <div className='form-group1Input container'>
            <input type='text' className='' value={Email} disabled />
          </div>
        </div>

        <div className='form-group1 container'>
          <div>Mobile:</div>
          <div className='form-group1Input container'>
            <input type='text' className='' value={Mobile} disabled />
          </div>
        </div>

        <div className='form-group1 container'>
          <div>Date of Birth:</div>
          <div className='form-group1Input container'>
            <input type='date' className='' />
          </div>
        </div>

        <button className='btn btn-success my-2' type='submit'>
          Next
        </button>
      </form>
    </div>
  );
}

export default TotalInfo;
