import React, { useState } from 'react';
import './PropertyForm.css';
import PopupModal from './PopupModal';
import { usePropertyContext } from '../InsuranceForms/PropertyContext';

function PropertyForm() {
  // State variables using React Hooks
  const [currentMarketValue, setCurrentMarketValue] = useState('');
  const [carpetArea, setCarpetArea] = useState('');
  const [pincode, setPincode] = useState('');
  const [buildingAge, setBuildingAge] = useState('0');
  const [propertyEffect, setPropertyEffect] = useState('No');
  const [security24x7, setSecurity24x7] = useState('No');
  const [isSalaried, setIsSalaried] = useState('Yes');
  const { updatePropertyData } = usePropertyContext(); // Using the context hook
  const [showPremiumPrices, setShowPremiumPrices] = useState(false);
  const [errorMessages, setErrorMessages] = useState({
    currentMarketValue: '',
    carpetArea: '',
    pincode: '',
    buildingAge: '',
  });
  const [totalSum, setTotalSum] = useState(0);
  const [popupMessage, setPopupMessage] = useState(null);

  // Function to handle input changes and real-time validation
  const handleInputChange = (fieldName, value, regex, errorMessage) => {
    if (!regex.test(value)) {
      setErrorMessages((prevErrors) => ({
        ...prevErrors,
        [fieldName]: errorMessage,
      }));
    } else {
      setErrorMessages((prevErrors) => ({ ...prevErrors, [fieldName]: '' }));
      // Update the state with the valid input
      switch (fieldName) {
        case 'currentMarketValue':
          setCurrentMarketValue(value);
          break;
        case 'carpetArea':
          setCarpetArea(value);
          break;
        case 'pincode':
          setPincode(value);
          break;
        case 'buildingAge':
          setBuildingAge(value);
          break;
        // Add cases for other input fields
        default:
          break;
      }
    }
  };

  // Function to handle combined actions
  const handleCombined = () => {
    handleProceed();
    handleCalculateSum();
    setShowPremiumPrices(true);
  };

  // Function to display an alert message
  const showAlert = (message) => {
    const alertContainer = document.createElement('div');
    alertContainer.className = 'alert-message';

    const alertMessage = document.createTextNode(message);
    alertContainer.appendChild(alertMessage);

    const closeBtn = document.createElement('span');
    closeBtn.className = 'close-btn';
    closeBtn.innerHTML = '&times;';
    closeBtn.onclick = () => alertContainer.remove();

    alertContainer.appendChild(closeBtn);

    document.body.appendChild(alertContainer);
  };

  // Function to handle form submission
  const handleProceed = () => {
    const isValid = validateInputs();

    if (isValid) {
      // Extracting and processing form data
      const propertyValue = parseInt(currentMarketValue, 10);
      const areaValue = parseInt(carpetArea, 10);
      const pincodeValue = parseInt(pincode, 10);
      const totalPropertyValue = propertyValue * 0.01;
      const forSecurity24x7 = propertyValue * 0.01;

      const formData = {
        propertyValue,
        currentMarketValue: totalPropertyValue,
        carpetArea: areaValue,
        pincode: pincodeValue,
        propertyEffect,
        forSecurity24x7,
        buildingAge,
        isSalaried,
      };
      updatePropertyData(formData); // Using the context function to update data
      console.log('Form submitted:', formData);
    } else {
      console.log('Form submission failed. Please enter valid numbers.');
    }
  };

  // Function to calculate the total sum
  const handleCalculateSum = () => {
    const propertyValue = parseInt(currentMarketValue, 10);
    const forSecurity24x7 = propertyValue * 0.008;
    const propertyEffectValue = propertyValue * 0.007;
    const buildingAgeValue = parseInt(buildingAge, 10);

    const additionalCost =
      (buildingAgeValue >= 0 && buildingAgeValue <= 5 ? 100 : 0) +
      (buildingAgeValue > 5 && buildingAgeValue <= 10 ? 200 : 0) +
      (buildingAgeValue > 10 && buildingAgeValue <= 15 ? 300 : 0) +
      (buildingAgeValue > 15 && buildingAgeValue <= 20 ? 400 : 0) +
      (buildingAgeValue > 20 && buildingAgeValue <= 25 ? 500 : 0) +
      (buildingAgeValue > 25 && buildingAgeValue <= 30 ? 600 : 0);

    const sum =
      additionalCost +
      (propertyEffect === 'Yes' ? 0 : propertyEffectValue) +
      (security24x7 === 'Yes' ? 0 : forSecurity24x7) +
      (isSalaried === 'Yes' ? 100 : 0);

    setTotalSum(sum * 0.5);

    if (propertyEffect === 'Yes') {
      showAlert('Due to being affected by floods/earthquake in the past 5 years, you are not eligible.');
      return;
    }

    if (isSalaried === 'No') {
      showAlert('Not a salaried individual. So, you are not eligible.');
      window.location.reload(); // Reload the page
      return;
    }

    console.log(`Total Sum: ${sum}`);
  };

  // Function to validate form inputs
  const validateInputs = () => {
    const numberRegex = /^\d*$/;
    let isValid = true;

    // Function to validate a field and update error messages
    const validateField = (fieldName, value, regex, errorMessage) => {
      if (!regex.test(value)) {
        setErrorMessages((prevErrors) => ({
          ...prevErrors,
          [fieldName]: errorMessage,
        }));
        isValid = false;
      } else {
        setErrorMessages((prevErrors) => ({ ...prevErrors, [fieldName]: '' }));
      }
    };

    validateField('currentMarketValue', currentMarketValue, numberRegex, 'Please enter a valid number.');
    validateField('carpetArea', carpetArea, numberRegex, 'Please enter a valid number.');
    validateField('pincode', pincode, /^\d{6}$/, 'Please enter a valid 6-digit pincode.');
    validateField('buildingAge', buildingAge, numberRegex, 'Please enter a valid number.');

    return isValid;
  };

  // JSX structure of the component
  return (
    <div className='org'>
      <div className='bg'>
        <h2>Prediction for the Premium Calculation</h2>
        <div className='bg1'>
          {/* ... (unchanged) */}
        </div>
      </div>
    </div>
  );
}

export default PropertyForm;
