import React, { useState } from 'react';
import './PropertyForm.css';
import PopupModal from './PopupModal';
import axios from 'axios';
import { usePropertyContext } from '../InsuranceForms/PropertyContext';

function PropertyForm() {
  const [formData, setFormData] = useState({
    currentMarketValue: '',
    carpetArea: '',
    pincode: '',
    buildingAge: '0',
    propertyEffect: 'No',
    security24x7: 'No',
    isSalaried: 'Yes'
  });

  const [showPremiumPrices, setShowPremiumPrices] = useState(false);
  const [totalSum, setTotalSum] = useState(0);
  const [popupMessage, setPopupMessage] = useState(null);
  const [errorMessages, setErrorMessages] = useState({
    currentMarketValue: '',
    carpetArea: '',
    pincode: '',
    buildingAge: '',
    duration: '',
  });

  const { updatePropertyData } = usePropertyContext(); // Using the context hook

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/property/add', formData);
      console.log('Property details saved:', response.data);
      // Handle success (e.g., show a success message)
    } catch (error) {
      console.error('Error saving property details:', error);
      // Handle error (e.g., show an error message)
    }
  };

  const handleProceed = () => {
    // Extracting and processing form data
    const propertyValue = parseInt(formData.currentMarketValue, 10);
    const areaValue = parseInt(formData.carpetArea, 10);
    const pincodeValue = parseInt(formData.pincode, 10);
    const totalPropertyValue = propertyValue * 0.01;
    const forSecurity24x7 = propertyValue * 0.01;

    const updatedFormData = {
      ...formData,
      propertyValue,
      currentMarketValue: totalPropertyValue,
      carpetArea: areaValue,
      pincode: pincodeValue
    };

    updatePropertyData(updatedFormData); // Using the context function to update data
    console.log('Form submitted:', updatedFormData);
  };

  const handleCalculateSum = () => {
    const propertyValue = parseInt(formData.currentMarketValue, 10);
    const forSecurity24x7 = propertyValue * 0.008;
    const propertyEffectValue = propertyValue * 0.007;
    const buildingAgeValue = parseInt(formData.buildingAge, 10);

    // Calculating additional cost based on building age
    const additionalCost =
      (buildingAgeValue >= 0 && buildingAgeValue <= 5 ? 100 : 0) +
      (buildingAgeValue > 5 && buildingAgeValue <= 10 ? 200 : 0) +
      (buildingAgeValue > 10 && buildingAgeValue <= 15 ? 300 : 0) +
      (buildingAgeValue > 15 && buildingAgeValue <= 20 ? 400 : 0) +
      (buildingAgeValue > 20 && buildingAgeValue <= 25 ? 500 : 0) +
      (buildingAgeValue > 25 && buildingAgeValue <= 30 ? 600 : 0);

    // Calculating total sum
    const sum =
      additionalCost +
      (formData.propertyEffect === 'Yes' ? 0 : propertyEffectValue) +
      (formData.security24x7 === 'Yes' ? 0 : forSecurity24x7) +
      (formData.isSalaried === 'Yes' ? 100 : 0);

    setTotalSum(sum * 0.5);

    if (formData.propertyEffect === 'Yes') {
      showAlert('Due to being affected by floods/earthquake in the past 5 years, you are not eligible.');
      return;
    }

    if (formData.isSalaried === 'No') {
      showAlert('Not a salaried individual. So, you are not eligible.');
      window.location.reload(); // Reload the page
      return;
    }

    console.log(`Total Sum: ${sum}`);
  };

  const handleCombined = () => {
    handleProceed();
    handleCalculateSum();
    setShowPremiumPrices(true);
  };

  const showAlert = (message) => {
    const alertContainer = document.createElement('div');
    alertContainer.className = 'alert-message';

    const alertMessage = document.createTextNode(message);
    alertContainer.appendChild(alertMessage);

    const closeBtn = document.createElement('span');
    closeBtn.className = 'close-btn';
    closeBtn.innerHTML = '&times;';
    closeBtn.onclick = () => alertContainer.remove();

    alertContainer.appendChild(closeBtn);

    document.body.appendChild(alertContainer);
  };

  // Function to validate form inputs
  const validateInputs = () => {
    const numberRegex = /^[1-9][0-9]+$/;
    let isValid = true;

    const validateField = (fieldName, value, length) => {
      if (fieldName !== 'buildingAge') {
        if (!numberRegex.test(value) || (length && value.length !== length)) {
          setErrorMessages((prevErrors) => ({
            ...prevErrors,
            [fieldName]: length
              ? `Please enter a valid ${length}-digit number.`
              : 'Please enter a valid number.',
          }));
          isValid = false;
        } else if (fieldName === 'currentMarketValue' && parseInt(value, 10) <= 10000) {
          setErrorMessages((prevErrors) => ({
            ...prevErrors,
            [fieldName]: 'Please enter a value greater than 100000 for Current Market Value.',
          }));
          isValid = false;
        } else {
          setErrorMessages((prevErrors) => ({ ...prevErrors, [fieldName]: '' }));
        }
      }
    };

    validateField('currentMarketValue', formData.currentMarketValue);
    validateField('carpetArea', formData.carpetArea);
    validateField('pincode', formData.pincode, 6);
    validateField('buildingAge', formData.buildingAge, 1);

    return isValid;
  };

  // JSX structure of the component
  return (
    <div className='org'>
      <div className='bg'>
        <h2>Prediction for the Premium Calculation</h2>
        <div className='bg1'>
          <div className='form-container container'>
            <h2>Structure Details</h2>
            <form className='forinner container' onSubmit={handleSubmit}>
              <div className='form-group'>
                <label>Current Market Value of Property <span style={{ color: 'red' }}>*</span> :</label>
                <input
                  type='text'
                  className='form-control'
                  placeholder='ex : 012345'
                  value={formData.currentMarketValue}
                  onChange={handleChange}
                  name="currentMarketValue"
                />
                <p className='error-message'>{errorMessages.currentMarketValue}</p>
              </div>

              <div className='form-group'>
                <label>Carpet Area/sq.ft <span style={{ color: 'red' }}>*</span> :</label>
                <input
                  type='text'
                  className='form-control'
                  placeholder='ex : 1200'
                  value={formData.carpetArea}
                  onChange={handleChange}
                  name="carpetArea"
                />
                <p className='error-message'>{errorMessages.carpetArea}</p>
              </div>

              <div className='form-group'>
                <label>Enter Pincode <span style={{ color: 'red' }}>*</span> :</label>
                <input
                  type='text'
                  className='form-control'
                  placeholder='ex : 515001'
                  value={formData.pincode}
                  maxLength={6}
                  onChange={handleChange}
                  name="pincode"
                />
                <p className='error-message'>{errorMessages.pincode}</p>
              </div>

              <div className='form-group'>
                <label>Age of Building <span style={{ color: 'red' }}>*</span> :</label>
                <select
                  className='form-control form-select'
                  value={formData.buildingAge}
                  onChange={handleChange}
                  name="buildingAge"
                >
                  {[0, 5, 10, 15, 20, 25, 30].map((years) => (
                    <option key={years} value={years}>
                      {`${years}-${years + 5} years`}
                    </option>
                  ))}
                </select>
              </div>

              <div className='form-group'>
                <label>Has your property been affected by floods/earthquake in the past 5 years{' '}
                  <span style={{ color: 'red' }}>*</span> :</label>
                <select
                  className='form-control form-select'
                  value={formData.propertyEffect}
                  onChange={handleChange}
                  name="propertyEffect"
                >
                  <option value='Yes'>Yes</option>
                  <option value='No'>No</option>
                </select>
              </div>
              <button className='btn btn-success my-2' onClick={handleCombined}>
                Calculate
              </button>
            </form>
          </div>
          <div className='form-container container'>
          <div className='form-container container'>
  <h2>Security Measures</h2>
  <form className='forinner container'>
    <div className='form-group'>
      <label>24*7 Security <span style={{ color: 'red' }}>*</span> :</label>
      <select
        className='form-control form-select'
        value={formData.security24x7}
        onChange={(e) => handleChange(e, 'security24x7')}
      >
        <option value='Yes'>Yes</option>
        <option value='No'>No</option>
      </select>
    </div>

    <div className='form-group'>
      <label>Are you a salaried ? <span style={{ color: 'red' }}>*</span> :</label>
      <select
        className='form-control form-select'
        value={formData.isSalaried}
        onChange={(e) => handleChange(e, 'isSalaried')}
      >
        <option value='Yes'>Yes</option>
        <option value='No'>No</option>
      </select>
    </div>
  </form>
</div>

          </div>
        </div>
      </div>
    </div>
  );
}

export default PropertyForm;
