// PropertyForm.js
import React, { useState } from 'react';
import './PropertyForm.css';
import axios from 'axios'; // Import Axios for making HTTP requests
import PopupModal from './PopupModal';
import { usePropertyContext } from '../InsuranceForms/PropertyContext';

function PropertyForm() {
  // State variables using React Hooks
  const [currentMarketValue, setCurrentMarketValue] = useState('');
  const [carpetArea, setCarpetArea] = useState('');
  const [pincode, setPincode] = useState('');
  const [buildingAge, setBuildingAge] = useState('0');
  const [propertyEffect, setPropertyEffect] = useState('No');
  const [security24x7, setSecurity24x7] = useState('No');
  const [isSalaried, setIsSalaried] = useState('Yes');
  const { updatePropertyData } = usePropertyContext(); // Using the context hook
  const [showPremiumPrices, setShowPremiumPrices] = useState(false);
  
  const [errorMessages, setErrorMessages] = useState({
    currentMarketValue: '',
    carpetArea: '',
    pincode: '',
    buildingAge: '',
    duration: '',
  });
  const [totalSum, setTotalSum] = useState(0);
  const [popupMessage, setPopupMessage] = useState(null);

  // Function to handle combined actions
  const handleCombined = () => {
    handleProceed();
    handleCalculateSum();
    setShowPremiumPrices(true);
  };

  // Function to handle input validation for each character input
  const handleInputValidation = (e, fieldName) => {
    const value = e.target.value;
    const regex = /.+/; // Your validation regex here

    if (!regex.test(value)) {
      setErrorMessages(prevErrors => ({
        ...prevErrors,
        [fieldName]: 'Please enter a valid value.',
      }));
    } else {
      setErrorMessages(prevErrors => ({
        ...prevErrors,
        [fieldName]: '',
      }));
    }

    // Update state with the input value
    switch (fieldName) {
      case 'currentMarketValue':
        setCurrentMarketValue(value);
        break;
      case 'carpetArea':
        setCarpetArea(value);
        break;
      case 'pincode':
        setPincode(value);
        break;
      default:
        break;
    }
  };

  // Function to display an alert message
  const showAlert = (message) => {
    const alertContainer = document.createElement('div');
    alertContainer.className = 'alert-message';

    const alertMessage = document.createTextNode(message);
    alertContainer.appendChild(alertMessage);

    const closeBtn = document.createElement('span');
    closeBtn.className = 'close-btn';
    closeBtn.innerHTML = '&times;';
    closeBtn.onclick = () => alertContainer.remove();

    alertContainer.appendChild(closeBtn);

    document.body.appendChild(alertContainer);
  };

  // Function to handle form submission
  const handleProceed = () => {
    const isValid = Object.values(errorMessages).every(msg => msg === '');

    if (isValid) {
      // Extracting and processing form data
      const propertyValue = parseInt(currentMarketValue, 10);
      const areaValue = parseInt(carpetArea, 10);
      const pincodeValue = parseInt(pincode, 10);
      const forSecurity24x7 = propertyValue * 0.01;

      const formData = {
        propertyValue,
        currentMarketValue: propertyValue,
        carpetArea: areaValue,
        pincode: pincodeValue,
        propertyEffect,
        forSecurity24x7,
        buildingAge,
        isSalaried
      };

      // Send formData to backend API
      axios.post('http://localhost:9856/api/property/add', formData)
        .then(response => {
          console.log('Form data saved successfully:', response.data);
          // You can perform any additional actions here, such as showing a success message
        })
        .catch(error => {
          console.error('Error saving form data:', error);
          // Handle errors, such as displaying an error message to the user
        });

      updatePropertyData(formData); // Using the context function to update data
      console.log('Form submitted:', formData);
    } else {
      console.log('Form submission failed. Please enter valid numbers.');
    }
  };

  // Function to calculate the total sum
  const handleCalculateSum = () => {
    const propertyValue = parseInt(currentMarketValue, 10);
    const forSecurity24x7 = propertyValue * 0.008;
    const propertyEffectValue = propertyValue * 0.007;
    const buildingAgeValue = parseInt(buildingAge, 10);

    const additionalCost =
      (buildingAgeValue >= 0 && buildingAgeValue <= 5  ? 100 : 0) +
      (buildingAgeValue > 5 && buildingAgeValue <= 10 ? 200 : 0) +
      (buildingAgeValue > 10 && buildingAgeValue <= 15 ? 300 : 0) +
      (buildingAgeValue > 15 && buildingAgeValue <= 20 ? 400 : 0) +
      (buildingAgeValue > 20 && buildingAgeValue <= 25 ? 500 : 0) +
      (buildingAgeValue > 25 && buildingAgeValue <= 30 ? 600 : 0);
    const sum =
      additionalCost +
      (propertyEffect === 'Yes' ? 0 : propertyEffectValue) +
      (security24x7 === 'Yes' ? 0 : forSecurity24x7) +
      (isSalaried === 'Yes' ? 100 : 0);

    setTotalSum(sum * 0.5);
    
    if (propertyEffect === 'Yes') {
      showAlert('Due to being affected by floods/earthquake in the past 5 years, you are not eligible.');
      return;
    }

    if (isSalaried === 'No') {
      showAlert('Not a salaried individual. So, you are not eligible.');
      window.location.reload(); // Reload the page
      return;
    }

    console.log(`Total Sum: ${sum}`);
  };

  // JSX structure of the component
  return (
    <div className='org'>
      <div className='bg'>
        <h2>Prediction for the Premium Calculation</h2>
        <div className='bg1'>
          <div className='form-container container'>
            <h2>Structure Details</h2>
            <form className='forinner container'>
              <div className='form-group'>
                <label>Current Market Value of Property <span style={{ color: 'red' }}>*</span> :</label>
                <input
                  type='text'
                  className='form-control'
                  placeholder='ex : 012345'
                  maxLength={12}
                  value={currentMarketValue}
                  onInput={(e) => handleInputValidation(e, 'currentMarketValue')}
                />
                <p className='error-message'>{errorMessages.currentMarketValue}</p>
              </div>

              <div className='form-group'>
                <label>Carpet Area/sq.ft <span style={{ color: 'red' }}>*</span> :</label>
                <input
                  type='text'
                  className                  ='form-control'
                  placeholder='ex : 1200'
                  value={carpetArea}
                  maxLength={5}
                  onInput={(e) => handleInputValidation(e, 'carpetArea')}
                />
                <p className='error-message'>{errorMessages.carpetArea}</p>
              </div>

              <div className='form-group'>
                <label>Enter Pincode <span style={{ color: 'red' }}>*</span> :</label>
                <input
                  type='text'
                  className='form-control'
                  placeholder='ex : 515001'
                  value={pincode}
                  maxLength={6}
                  onInput={(e) => handleInputValidation(e, 'pincode')}
                />
                <p className='error-message'>{errorMessages.pincode}</p>
              </div>

              <div className='form-group'>
                <label>Age of Building <span style={{ color: 'red' }}>*</span> :</label>
                <select
                  className='form-control form-select'
                  value={buildingAge}
                  onChange={(e) => setBuildingAge(e.target.value)}
                >
                  {[0, 5, 10, 15, 20, 25, 30].map((years) => (
                    <option key={years} value={years}>
                      {`${years}-${years + 5} years`}
                    </option>
                  ))}
                </select>
              </div>

              <div className='form-group'>
                <label>Has your property been affected by floods/earthquake in the past 5 years{' '}
                  <span style={{ color: 'red' }}>*</span> :</label>
                <select
                  className='form-control form-select'
                  value={propertyEffect}
                  onChange={(e) => setPropertyEffect(e.target.value)}
                >
                  <option value='Yes'>Yes</option>
                  <option value='No'>No</option>
                </select>
              </div>
            </form>
          </div>
          <div className='form-container container'>
            <h2>Security Measures</h2>
            <form className='forinner container'>
              <div className='form-group'>
                <label>24*7 Security <span style={{ color: 'red' }}>*</span> :</label>
                <select
                  className='form-control form-select'
                  value={security24x7}
                  onChange={(e) => setSecurity24x7(e.target.value)}
                >
                  <option value='Yes'>Yes</option>
                  <option value='No'>No</option>
                </select>
              </div>

              <div className='form-group'>
                <label>Are you a salaried ? <span style={{ color: 'red' }}>*</span> :</label>
                <select
                  className='form-control form-select'
                  value={isSalaried}
                  onChange={(e) => setIsSalaried(e.target.value)}
                >
                  <option value='Yes'>Yes</option>
                  <option value='No'>No</option>
                </select>
              </div>
            </form>

            {showPremiumPrices && (
              <>
                <h2>Premium Prices As per Years</h2>
                {!Object.values(errorMessages).some((errorMessage) => errorMessage) && (
                  <div className='d-flex'>
                    <h4
                      className='btn btn-primary mx-1'
                      onClick={() => setPopupMessage((totalSum + (totalSum * 0.28)).toFixed(2))}
                      >
                      1 year {(totalSum + (totalSum * 0.28)).toFixed(2)}/-
                    </h4>
                    <h4
                      className='btn btn-primary mx-1'
                      onClick={() => setPopupMessage(`2 years ${(2*totalSum + (totalSum * 0.24)).toFixed(2)}/-`)}
                    >
                      2 years {(2*totalSum + (totalSum * 0.24)).toFixed(2)}/-
                    </h4>
                    <h4
                      className='btn btn-primary mx-1'
                      onClick={() => setPopupMessage(`3 years ${(3*totalSum + (totalSum * 0.20)).toFixed(2)}/-`)}
                    >
                      3 years {(3*totalSum + (totalSum * 0.12)).toFixed(2)}/-
                    </h4>
                    <h4
                      className='btn btn-primary mx-1'
                      onClick={() => setPopupMessage(`4 years ${(4*totalSum + (totalSum * 0.16)).toFixed(2)}/-` )}
                    >
                      4 years {(4*totalSum + (totalSum * 0.12)).toFixed(2)}/-
                    </h4>
                    <h4
                      className='btn btn-primary mx-1' 
                      onClick={() => setPopupMessage(`5 years ${(5*totalSum + (totalSum * 0.12)).toFixed(2)}/-`)}
                    >
                      5 years  {(5*totalSum + (totalSum * 0.12)).toFixed(2)}/-
                    </h4>
                  </div>
                )}
              </>
            )}

            {popupMessage && (
              <PopupModal message={popupMessage} onClose={() => setPopupMessage(null)} />
            )}

            {propertyEffect === 'Yes' && isSalaried === 'No' ? null : (
              <button className='btn btn-success my-2' onClick={handleCombined}>
                Calculate
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default PropertyForm;
