// TotalInfo.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useUserContext } from './UserContext';
import { usePropertyContext } from '../InsuranceForms/PropertyContext';

import './TotalInfo.css';

function TotalInfo() {
  // Access user data and navigation hook
  const { userData } = useUserContext();
  const navigate = useNavigate();
  const { propertyValue, buildingAge, carpetArea, pincode, popupMessage } = usePropertyContext();
  const [minDate, setMinDate] = useState('');
  
  // State variables for form input fields
  const [FullNamePrefix, setFullNamePrefix] = useState('Mr.');
  const [FullName, setFullName] = useState('');
  const [PanCard, setPanCard] = useState('');
  const [DateofBirth, setDateofBirth] = useState('');
  const [FlatNo, setFlatNo] = useState('');
  const [AreaStreet, setAreaStreet] = useState('');
  const [pincodee, setPincodee] = useState('');
  const [IsCurrentAddress, setIsCurrentAddress] = useState('');
  const [CurrentAddress, setCurrentAddress] = useState('');

  // State variable for displaying a message
  const [message, setMessage] = useState('');

  // State variable for error messages
  const [errorMessages, setErrorMessages] = useState({
    FullName: '',
    PanCard: '',
    DateofBirth: '',
    FlatNo: '',
    AreaStreet: '',
    CurrentAddress: '',
    pincode: '',
  });

  // Function to validate individual form fields
  const validateField = (fieldName, value, regex, errorMessage) => {
    if (!value || (regex && !regex.test(value))) {
      setErrorMessages((prevErrors) => ({
        ...prevErrors,
        [fieldName]: errorMessage,
      }));
      return false;
    } else {
      setErrorMessages((prevErrors) => ({ ...prevErrors, [fieldName]: '' }));
      return true;
    }
  };

  // Function to validate all form inputs
  const validateInputs = () => {
    const panCardRegex = /^[A-Z]{5}[0-9]{4}[A-Z]$/;
    const dateOfBirthRegex = /^\d{1,2}\/\d{1,2}\/\d{4}$/;
    const flatNoRegex = /^[A-Za-z0-9\s]+$/;
    const areaStreetRegex = /^[A-Za-z0-9\s]+$/;

    let isValid = true;

    isValid = isValid && validateField('FullName', FullName, /.+/, 'Please enter your full name.');
    isValid = isValid && validateField('PanCard', PanCard, panCardRegex, 'Please enter a valid PanCard.');
    isValid = isValid && validateField('DateofBirth', DateofBirth, dateOfBirthRegex, 'Please enter a valid date of birth.');
    isValid = isValid && validateField('FlatNo', FlatNo, flatNoRegex, 'Please enter a valid flat number.');
    isValid = isValid && validateField('AreaStreet', AreaStreet, areaStreetRegex, 'Please enter a valid area or street.');

    if (IsCurrentAddress === 'no') {
      isValid = isValid && validateField('CurrentAddress', CurrentAddress, areaStreetRegex, 'Please enter a valid current address.');
      isValid = isValid && validateField('pincode', pincodee, /^\d{6}$/, 'Please enter a valid pincode.');
    }

    return isValid;
  };

  // Function to handle input changes
  const handleInputChange = (fieldName, value, regex, errorMessage) => {
    switch (fieldName) {
      case 'FullNamePrefix':
        setFullNamePrefix(value);
        break;
      case 'FullName':
        setFullName(value);
        break;
      case 'PanCard':
        setPanCard(value);
        break;
      case 'DateofBirth':
        setDateofBirth(value);
        break;
      case 'FlatNo':
        setFlatNo(value);
        break;
      case 'AreaStreet':
        setAreaStreet(value);
        break;
      case 'CurrentAddress':
        setCurrentAddress(value);
        break;
      case 'pincodee':
        setPincodee(value);
        break;
      default:
        break;
    }

    validateField(fieldName, value, regex, errorMessage);
  };

  // Function to handle form submission
  const handleProceed = () => {
    const isValid = validateInputs();

    if (isValid) {
      console.log('Proceeding to the next step...');
      // You can access user data from context here: userData.FullName, userData.Mobile, userData.Email
      setMessage('Your custom message here.');
      navigate('/payment');
    } else {
      console.log('Form submission failed. Please check the fields.');
    }
  };

  useEffect(() => {
    const today = new Date();
    const minDate = new Date(today.getFullYear() - 21, today.getMonth(), today.getDate());
    setMinDate(minDate.toISOString().split('T')[0]);
  }, []);

  // JSX structure of the component
  return (
    <div className='org12'>
      <h2>Fill the details</h2>
      <div className='forDispFlex'>
        <div className='fortablecls'>
          <h3>user Details</h3>
          <table className='fortable container '>
            <tr>
              <td className='tbfont'>FullName:</td>
              <td>
                <select
                  value={FullNamePrefix}
                  onChange={(e) => setFullNamePrefix(e.target.value)}
                >
                  <option>Mr.</option>
                  <option>Mrs.</option>
                  <option>Ms.</option>
                </select>
              </td>
              <td className='tbfont1'>
                {FullNamePrefix} {userData.FullName}
              </td>
            </tr>
            <tr>
              <td className='tbfont'>MobileNo</td>
              <td>:</td>
              <td className='tbfont1'>{userData.Mobile}</td>
            </tr>
            <tr>
              <td className='tbfont'>Email.Id</td>
              <td>:</td>
              <td className='tbfont1'>{userData.Email}</td>
            </tr>
          </table>
          <div>
            <h3>Premium Data</h3>
            <table className='fortable container '>
              <tr>
                <th></th>
                <th></th>
                <th></th>
              </tr>
              <tr>
                <th className='tbfont'>PropertyValue</th>
                <th>: </th>
                <th className='tbfont1'>{propertyValue}</th>
              </tr>
              <tr>
                <th className='tbfont'>CarpetValue</th>
                <th>: </th>
                <th className='tbfont1'>{carpetArea}</th>
              </tr>
              <tr>
                <th className='tbfont'>BuildingAge</th>
                <th>:</th>
                <th className='tbfont1'>{buildingAge} years</th>
              </tr>
              <tr>
                <th className='tbfont'>Total</th>
                <th>:</th>
                <th className='tbfont1'>
                  <h6>{popupMessage}</h6>
                </th>
              </tr>
            </table>
          </div>
        </div>

        <div className='forinner11d'>
          <form className='forinner11 container'>
            <h3>Enter PanCard details</h3>
            <div className='form-group11 container'>
              <div>FullName:</div>
              <div className='form-group11Input container'>
                <input
                  type='text'
                  className=''
                  placeholder='Enter your full name'
                  value={FullName}
                  onChange={(e) => handleInputChange('FullName', e.target.value, /.+/, 'Please enter your full name.')}
                />
                <p className='error-message11' style={{ color: 'red', textAlign: 'center' }}>
                  {errorMessages.FullName}
                </p>
              </div>
            </div>

            <div className='form-group11 container'>
              <div>PanCard:</div>
              <div className='form-group11Input container'>
                <input
                  type='text'
                  className=''
                  placeholder='ex: ABCDE1234F'
                  value={PanCard}
                  onChange={(e) =>
                    handleInputChange('PanCard', e.target.value, /^[A-Z]{5}[0-9]{4}[A-Z]$/, 'Please enter a valid PanCard.')
                  }
                />
                <p className='error-message11' style={{ color: 'red', textAlign: 'center' }}>{errorMessages.PanCard}</p>
              </div>
            </div>

            <div className='form-group11 container'>
              <div>DateofBirth:</div>
              <div className='form-group11Input container'>
                <input
                  type='text'
                  className=''
                  placeholder='ex: dd/mm/yyyy'
                  value={DateofBirth}
                  onChange={(e) =>
                    handleInputChange('DateofBirth', e.target.value, /^\d{1,2}\/\d{1,2}\/\d{4}$/, 'Please enter a valid date of birth.')
                  }
                />
                <p className='error-message11' style={{ color: 'red', textAlign: 'center' }}>
                  {errorMessages.DateofBirth}
                </p>
              </div>
            </div>

            <h3>property details</h3>

            <div className='form-group11 container'>
              <h5>
                pincode:<span style={{ color: 'gray' }}>{pincode}</span>
              </h5>
              <div>FlatNo/House No:</div>
              <div className='form-group11Input container'>
                <input
                  type='text'
                  className=''
                  placeholder='ex: 123'
                  value={FlatNo}
                  onChange={(e) =>
                    handleInputChange('FlatNo', e.target.value, /^[A-Za-z0-9\s]+$/, 'Please enter a valid flat number.')
                  }
                />
                <p className='error-message11' style={{ color: 'red', textAlign: 'center' }}>
                  {errorMessages.FlatNo}
                </p>
              </div>
            </div>

            <div className='form-group11 container'>
              <div>Area/Street:</div>
              <div className='form-group11Input container'>
                <input
                  type='text'
                  className=''
                  placeholder='ex: Main Street'
                  value={AreaStreet}
                  onChange={(e) =>
                    handleInputChange('AreaStreet', e.target.value, /^[A-Za-z0-9\s]+$/, 'Please enter a valid area or street.')
                  }
                />
                <p className='error-message11' style={{ color: 'red', textAlign: 'center' }}>
                  {errorMessages.AreaStreet}
                </p>
              </div>
            </div>

            <div className='form-group11 container'>
              <div>Is the address mentioned above your current address:</div>
              <div className='form-group11Input container'>
                <select onChange={(e) => setIsCurrentAddress(e.target.value)}>
                  <option value=''>Select</option>
                  <option value='yes'>Yes</option>
                  <option value='no'>No</option>
                </select>
              </div>
            </div>

            {IsCurrentAddress === 'no' && (
              <div>
                <div className='form-group11 container'>
                  <hr />
                  <div>Current Address:</div>
                  <br />
                  <div className='form-group11 container'>
                    <div className='form-group'>
                      <label>
                        Enter Pincode <span style={{ color: 'red' }}>*</span> :
                      </label>
                      <input
                        type='text'
                        className='form-control'
                        placeholder='ex : 515001'
                        value={pincodee}
                        onChange={(e) => setPincodee(e.target.value)}
                      />
                      <p className='error-message'>{errorMessages.pincode}</p>
                    </div>
                  </div>
                  <div>FlatNo/House No:</div>
                  <div className='form-group11Input container'>
                    <input
                      type='text'
                      className=''
                      placeholder='ex: 123'
                      value={FlatNo}
                      onChange={(e) =>
                        handleInputChange('FlatNo', e.target.value, /^[A-Za-z0-9\s]+$/, 'Please enter a valid flat number.')
                      }
                    />
                    <p className='error-message11' style={{ color: 'red', textAlign: 'center' }}>
                      {errorMessages.FlatNo}
                    </p>
                  </div>

                  <div className='form-group11 container'>
                    <div>Area/Street:</div>
                    <div className='form-group11Input container'>
                      <input
                        type='text'
                        className=''
                        placeholder='ex: Main Street'
                        value={AreaStreet}
                        onChange={(e) =>
                          handleInputChange('AreaStreet', e.target.value, /^[A-Za-z0-9\s]+$/, 'Please enter a valid area or street.')
                        }
                      />
                      <p className='error-message11' style={{ color: 'red', textAlign: 'center' }}>
                        {errorMessages.AreaStreet}
                      </p>
                    </div>
                  </div>
                </div>
              </div>
            )}
            <button className='btn btn-success my-2 ' type='button' onClick={handleProceed}>
              Proceed
            </button>
          </form>
        </div>
        <div></div>
      </div>
    </div>
  );
}

export default TotalInfo;
