// SignInPage.js
import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import './SignInPage.css';

const SignInPage = () => {
  const history = useHistory();

  const [fullName, setFullName] = useState('');
  const [mobile, setMobile] = useState('');
  const [email, setEmail] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [validationErrors, setValidationErrors] = useState({
    fullName: '',
    mobile: '',
    email: '',
    newPassword: '',
  });

  useEffect(() => {
    // You can use the history object here if needed
  }, [history]);

  const handleSignIn = () => {
    if (validateInputs()) {
      // Perform sign-in logic here, such as sending data to the server
      // You can add further logic for API calls, etc.
      console.log('Sign In Data:', { fullName, mobile, email, newPassword });
      // Redirect to another page if needed
      history.push('/dashboard');
    } else {
      console.log('Validation failed. Please check the input values.');
    }
  };

  const validateInputs = () => {
    const nameRegex = /^[a-zA-Z ]+$/;
    const mobileRegex = /^[0-9]+$/;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const passwordRegex = /^(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{5,}$/;

    let isValid = true;

    const validateField = (fieldName, value, regex, errorMessage) => {
      if (!regex.test(value)) {
        setValidationErrors((prevErrors) => ({
          ...prevErrors,
          [fieldName]: errorMessage,
        }));
        isValid = false;
      } else {
        setValidationErrors((prevErrors) => ({ ...prevErrors, [fieldName]: '' }));
      }
    };

    validateField('fullName', fullName, nameRegex, 'Please enter a valid full name.');
    validateField('mobile', mobile, mobileRegex, 'Please enter a valid mobile number.');
    validateField('email', email, emailRegex, 'Please enter a valid email address.');
    validateField(
      'newPassword',
      newPassword,
      passwordRegex,
      'Password must contain at least one uppercase letter, one number, one special character, and be at least 5 characters long.'
    );

    return isValid;
  };

  return (
    <div className='signin-container'>
      <h2>Sign In</h2>
      <form className='signin-form'>
        <div className='form-group'>
          <label>Full Name:</label>
          <input
            type='text'
            className='form-control'
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
          />
          <p className='error-message'>{validationErrors.fullName}</p>
        </div>

        <div className='form-group'>
          <label>Mobile:</label>
          <input
            type='text'
            className='form-control'
            value={mobile}
            onChange={(e) => setMobile(e.target.value)}
          />
          <p className='error-message'>{validationErrors.mobile}</p>
        </div>

        <div className='form-group'>
          <label>Email:</label>
          <input
            type='email'
            className='form-control'
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
          <p className='error-message'>{validationErrors.email}</p>
        </div>

        <div className='form-group'>
          <label>New Password:</label>
          <input
            type='password'
            className='form-control'
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
          />
          <p className='error-message'>{validationErrors.newPassword}</p>
        </div>

        <button type='button' className='btn btn-success' onClick={handleSignIn}>
          Sign In
        </button>
      </form>
    </div>
  );
};

export default SignInPage;
