import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Paymentpage.css';

function Paymentpage() {
  const [selectedOption, setSelectedOption] = useState('');
  const navigate = useNavigate();

  const handleContinue = () => {
    // Check if an option is selected before navigating
    if (selectedOption) {
      // You can add logic here if needed
      console.log(`Selected option: ${selectedOption}`);
      
      // Navigate to the next page
      navigate('/nextpage');
    } else {
      alert('Please select a payment option.');
    }
  };

  return (
    <div className='pyhd'>
      <p>Pay through:</p>
      <label>
        <input
          type='radio'
          value='phonePe'
          checked={selectedOption === 'phonePe'}
          onChange={() => setSelectedOption('phonePe')}
        />
        Phone Pe
      </label>
      <label>
        <input
          type='radio'
          value='zaPay'
          checked={selectedOption === 'zaPay'}
          onChange={() => setSelectedOption('zaPay')}
        />
        ZaPay
      </label>
      <button className='mx-3 my-3 pylk' onClick={handleContinue}>
        Continue
      </button>
    </div>
  );
}

export default Paymentpage;
