import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function SignInPage() {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    FullName: '',
    Mobile: '',
    Email: '',
    Password: '',
  });
  const [errorMessages, setErrorMessages] = useState({
    FullName: '',
    Mobile: '',
    Email: '',
    Password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateInputs()) {
      axios.post('http://localhost:9856/api/signin', formData)
        .then(response => {
          console.log('Sign in successful:', response.data);
          navigate('/success');
        })
        .catch(error => {
          console.error('Sign in failed:', error);
        });
    } else {
      console.log('Form submission failed. Please check the fields.');
    }
  };

  const validateInputs = () => {
    const nameRegex = /^[A-Za-z][A-Za-z. ]{1,18}[A-Za-z. ]$/;
    const mobileRegex = /^[987][0-9]{9}$/;
    const emailRegex = /^[^\s!@#$%^&*@]+@[^\s@]+\.(com|in)$/;
    const passwordRegex = /(?=.*[0-9])(?=.*[@])(?=.*[A-Z])[A-Za-z0-9!@#$%^&*]{8,15}$/;

    let isValid = true;
    const errors = {};

    for (const [key, value] of Object.entries(formData)) {
      switch (key) {
        case 'FullName':
          if (!value || !nameRegex.test(value)) {
            errors.FullName = 'Please enter a valid full name.';
            isValid = false;
          }
          break;
        case 'Mobile':
          if (!value || !mobileRegex.test(value)) {
            errors.Mobile = 'Please enter a valid 10-digit mobile number.';
            isValid = false;
          }
          break;
        case 'Email':
          if (!value || !emailRegex.test(value)) {
            errors.Email = 'Please enter a valid email address.';
            isValid = false;
          }
          break;
        case 'Password':
          if (!value || !passwordRegex.test(value)) {
            errors.Password = 'Password must be at least 8 characters, contain at least one letter, one number, and one special character.';
            isValid = false;
          }
          break;
        default:
          break;
      }
    }

    setErrorMessages(errors);
    return isValid;
  };

  return (
    <div className="sign-in-page">
      <h2>Sign In</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Full Name:</label>
          <input type="text" name="FullName" value={formData.FullName} onChange={handleChange} />
          <p className="error-message">{errorMessages.FullName}</p>
        </div>
        <div className="form-group">
          <label>Mobile Number:</label>
          <input type="text" name="Mobile" value={formData.Mobile} onChange={handleChange} />
          <p className="error-message">{errorMessages.Mobile}</p>
        </div>
        <div className="form-group">
          <label>Email:</label>
          <input type="text" name="Email" value={formData.Email} onChange={handleChange} />
          <p className="error-message">{errorMessages.Email}</p>
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input type="password" name="Password" value={formData.Password} onChange={handleChange} />
          <p className="error-message">{errorMessages.Password}</p>
        </div>
        <button type="submit">Sign In</button>
      </form>
    </div>
  );
}

export default SignInPage;
