import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; 
import { useUserContext } from '../ListOfPolicies/UserContext';
import './SignInPage.css'; // Import custom CSS file

function SignInPage() {
  const { updateUserData } = useUserContext();
  const navigate = useNavigate();
  const [FullName, setFullName] = useState('');
  const [Mobile, setMobile] = useState('');
  const [Email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessages, setErrorMessages] = useState({
    FullName: '',
    Mobile: '',
    Email: '',
    password: '',
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    const isValid = validateInputs();

    if (isValid) {
      const formData = {
        fullName: FullName,
        mobile: Mobile,
        email: Email,
        password: password,
      };

      axios.post('http://localhost:9856/api/signin/add', formData)
        .then(response => {
          updateUserData({ FullName, Mobile, Email });
          navigate('/success');
        })
        .catch(error => {
          console.error('Sign in failed:', error);
          if (error.response && error.response.status === 409) {
            setErrorMessages(prevErrors => ({
              ...prevErrors,
              Mobile: 'Mobile number already exists. Kindly switch to SignUp.',
            }));
          } else {
            setErrorMessages(prevErrors => ({
              ...prevErrors,
              Mobile: 'Number already exists. Kindly switch to Login.',
            }));
          }
        });
    }
  };

  const validateInputs = () => {
    const nameRegex = /^[A-Za-z][A-Za-z. ]{1,18}[A-Za-z. ]$/;
    const mobileRegex = /^[987][0-9]{9}$/;
    const emailRegex = /^[^\s!@#$%^&*@]+@[^\s@]+\.(com|in)$/;
    const passwordRegex = /(?=.*[0-9])(?=.*[@])(?=.*[A-Z])[A-Za-z0-9!@#$%^&*]{8,15}$/;
    let isValid = true;

    const validateField = (fieldName, value, regex, errorMessage) => {
      if (!value || (regex && !regex.test(value))) {
        setErrorMessages(prevErrors => ({
          ...prevErrors,
          [fieldName]: errorMessage,
        }));
        isValid = false;
      } else {
        setErrorMessages(prevErrors => ({ ...prevErrors, [fieldName]: '' }));
      }
    };

    validateField('FullName', FullName, nameRegex, 'Please enter a valid full name.');
    validateField('Mobile', Mobile, mobileRegex, 'Please enter a valid 10-digit mobile number.');
    validateField('Email', Email, emailRegex, 'Please enter a valid email address.');
    validateField('password', password, passwordRegex, 'Password must be at least 8 characters, contain at least one letter, one number, and one special character.');

    return isValid;
  };

  return (
    <div className='Mpg'>
      <div className='row justify-content-center'>
        <div className='col-md-4'>
          <form className='my-5 custom-form' onSubmit={handleSubmit}>
            <div className='form-group'>
              <label htmlFor='fullName'>Full Name:</label>
              <input
                type='text'
                className='form-control'
                id='fullName'
                placeholder='Enter your full name'
                value={FullName}
                onChange={(e) => setFullName(e.target.value)}
              />
              <p className='text-danger'>{errorMessages.FullName}</p>
            </div>

            <div className='form-group'>
              <label htmlFor='mobile'>Mobile No:</label>
              <input
                type='text'
                className='form-control'
                id='mobile'
                placeholder='Enter your mobile number'
                value={Mobile}
                onChange={(e) => setMobile(e.target.value)}
              />
              <p className='text-danger'>{errorMessages.Mobile}</p>
            </div>

            <div className='form-group'>
              <label htmlFor='email'>Email:</label>
              <input
                type='text'
                className='form-control'
                id='email'
                placeholder='Enter your email address'
                value={Email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <p className='text-danger'>{errorMessages.Email}</p>
            </div>

            <div className='form-group'>
              <label htmlFor='password'>Password:</label>
              <input
                type='password'
                className='form-control'
                id='password'
                placeholder='Enter your password'
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <p className='text-danger'>{errorMessages.password}</p>
            </div>

            <button className='btn btn-primary' type='submit'>
              Sign In
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default SignInPage;
